css:
/* i really want this to be global */
/* Vivaldi - Delayed color changer V2 */
/* Accent Color from Active Page */
.color-behind-tabs-off .tab-position .tab.active
{
    background-color: var(--colorAccentBg) !important;
    color: var(--colorAccentFg) !important;
    transition-delay: 0.15s !important;
    transition-duration: 0.4s !important;
    transition-timing-function: ease-in !important; 
}
.color-behind-tabs-off .tab-position .tab.active .tab-indicator.active
{
    background-color: var(--colorAccentBg) !important;
    color: var(--colorAccentFg) !important;
    transition-duration: 0.15s !important; 
    transition-timing-function: ease-in !important; 
}
.color-behind-tabs-off .toolbar-mainbar
{
    background-color: var(--colorAccentBg) !important;
    color: var(--colorAccentFg) !important;
    transition-delay: 0.4s !important;
    transition-duration: 1s !important; 
    transition-timing-function: ease-in !important; 
}
.color-behind-tabs-off .button-toolbar.button-narrow > button > span
{
    transition-delay: 0.5s !important;
    transition-duration: 1.2s !important; 
    transition-timing-function: ease-in !important; 
}
/* Apply Accent Color to Window */
.color-behind-tabs-on .tab-position .tab.active
{
    transition-delay: 0.15s !important; 
    transition-duration: 0.4s !important; 
    transition-timing-function: ease-in !important; 
}
.color-behind-tabs-on .tab-position .tab.active .tab-indicator.active
{
    transition-duration: 0.15s !important; 
    transition-timing-function: ease-in !important; 
}
.color-behind-tabs-on #header
{
    background-color: var(--colorAccentBg) !important;
    color: var(--colorImageForeground, var(--colorAccentFg)) !important;
    transition-delay: 0.4s !important;
    transition-duration: 1s !important; 
    transition-timing-function: ease-in !important; 
}
