css:
@-moz-document domain("mylearningbox.reseau-cd.fr") {
html, body {
    background-color:#2c2f33;
    background:#2c2f33;
    color:#99aab5;
    overflow-x:hidden;
}
#region-main, #navwrap, .nav .dropdown-menu li a:hover, .dropdown-menu.usermen li a:hover, #page-footer, #page-footer p {
    background-color:#2c2f33;
}
#navwrap {
    border-bottom: 3px solid #7289da;
}
.courses .coursebox.even, .courses .coursebox {
    background-color: #23272a!important;
}
.course_category_tree .category>.info, .dropdown-menu, select, #page .course-content ul li.section.main {
    background-color: #23272a;
    border:1px solid #23272a;
}
a, a:visited, .tabtree .tabrow0 li a, .dropdown-menu.usermen>li>a .jsenabled .block_settings .block_tree .tree_item.branch, .block_navigation .block_tree .tree_item.branch, .dropdown-menu a, .navbar .nav>li>a, input, button, select, textarea, #above-header .headermenu .dropdown-menu a, #above-header .headermenu .dropdown-toggle a, .nav .dropdown-menu li a, .block .header .title h2, h1, h2, h3, h4, h5, h6, .path-course-view .completionprogress, .form-label label {
    color: #ffffff!important;
}
.breadcrumb ul i, .breadcrumb li.lastli span {
    color: #ffffff;
}
#page-navbar, .breadcrumb, .block>.content, .block .header, #dockeditempanel .dockeditempanel_hd {
    background-color: #2c2f33!important;
}
#notice.box {
    border: 2px solid #7289da;
}
.path-mod-workshop .assessment-summary.graded .singlebutton input[type="submit"], .path-mod-workshop .example-summary.graded .singlebutton input[type="submit"], .btn-link, .btn-default, [data-role="next"], [data-role="previous"], input[type="submit"], input[type="button"], #searchform_button, a.submit, #notice .singlebutton + .singlebutton input, #page-login-index2 .forgetpass a, input.form-submit, input#id_submitbutton, input#id_submitbutton2, .path-admin .buttons input[type="submit"], td.submit input, #above-header, .navbar .nav>li>a:hover {
    background-color:#7289da!important;
}
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    background-color: #40444b;
    border: 1px solid #40444b;
}
.btn, .btn a, #above-header {
    border:1px solid #7289da;
}
table#explaincaps tbody>tr:nth-child(odd)>td, table#defineroletable tbody>tr:nth-child(odd)>td, table.grading-report tbody>tr:nth-child(odd)>td, table#listdirectories tbody>tr:nth-child(odd)>td, table.rolecaps tbody>tr:nth-child(odd)>td, table.userenrolment tbody>tr:nth-child(odd)>td, table#form tbody>tr:nth-child(odd)>td, form#movecourses table tbody>tr:nth-child(odd)>td, #page-admin-course-index .editcourse tbody>tr:nth-child(odd)>td, .forumheaderlist tbody>tr:nth-child(odd)>td, table.flexible tbody>tr:nth-child(odd)>td, .generaltable tbody>tr:nth-child(odd)>td, table#explaincaps tbody>tr:nth-child(odd)>th, table#defineroletable tbody>tr:nth-child(odd)>th, table.grading-report tbody>tr:nth-child(odd)>th, table#listdirectories tbody>tr:nth-child(odd)>th, table.rolecaps tbody>tr:nth-child(odd)>th, table.userenrolment tbody>tr:nth-child(odd)>th, table#form tbody>tr:nth-child(odd)>th, form#movecourses table tbody>tr:nth-child(odd)>th, #page-admin-course-index .editcourse tbody>tr:nth-child(odd)>th, .forumheaderlist tbody>tr:nth-child(odd)>th, table.flexible tbody>tr:nth-child(odd)>th, .generaltable tbody>tr:nth-child(odd)>th {
    background-color: #40444b;
}
#page-header-wrapper {
    background-image: url(https://aime23.fr/stylish/banner.jpg)!important;
}
li img:not(.userpicture):not(.defaultuserpic):not(.icon):not(.iconlarge):not(.activityicon) {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(https://aime23.fr/stylish/image1.jpg);
    background-size: 100% auto;
    padding-left: 100%;
    background-position-y:70%;
    /* Equal to width of new image */
}
li a img:not(.userpicture):not(.defaultuserpic):not(.icon):not(.iconlarge):not(.activityicon) {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(https://aime23.fr/stylish/image2.jpg);
    background-size: 100% auto;
    padding-left: 100%;
    /* Equal to width of new image */
}
.flexslider .slides img {
    height: 90vh;
}
.flex-caption {
    display: none;
}

.cellbg, .simple-ss {
    background:none;
}

#above-header .headermenu .dropdown-menu {
 
    margin-top: 8px!important;
    
}
ul.dropdown-menu li {
    background-color: #23272a;
}

.section li.modtype_assign div.activity-wrapper {
    border:none;
    background-color:#23272a;
}

#marketblocks .internalmarket, .navbar .nav>li {
    border:none;
}

.path-course-view li.activity span.autocompletion img, .path-course-view li.activity form.togglecompletion .btn {
    background-color:transparent!important;
}
}
