@namespace url(http://www.w3.org/1999/xhtml);

/* Let's fix the UF Survey Research Center's ugly little schedule adjusting page. */

@-moz-document domain("scheduler.bebr.ufl.edu") {

/* Available shift > Teal to light yellow */
td[bgcolor='#7FFFD4'] {background-color: #fff15f !important; color: #444 !important;}
td[bgcolor='#7FFFD4']:hover {background-color: #ffd40f !important; color: #444 !important;}
td[bgcolor='#7FFFD4'] a {color: #000 !important; text-decoration: underline !important;}
td[bgcolor='#7FFFD4'] a:hover {color: #6f088f !important; text-decoration: underline !important; font-weight: bold !important;}


/* Added shift > Blue to light green */
td[bgcolor='#6495ed'] {background-color: #8f7547 !important; color: #444 !important;}
td[bgcolor='#6495ed'] a {color: #000 !important; text-decoration: underline !important;}
td[bgcolor='#6495ed'] a:hover {color: #b4df6f !important; text-decoration: underline !important; font-weight: bold !important;}

/* Scheduled shift > shit brown to mocha */
td[bgcolor='#996600'] {background-color: #b4df6f !important; color: #444 !important;}
td[bgcolor='#996600'] a {color: #000 !important; text-decoration: underline !important;}
td[bgcolor='#996600'] a:hover {color: #1c8f08 !important; text-decoration: underline !important; font-weight: bold !important;}

/* Cancelled > Red to oxblood */
td[bgcolor='#ff0000'] {background-color: #3f140c !important; color: #444 !important; border: 1px solid #ddd !important;}
td[bgcolor='#ff0000'] a {color: #ddd !important; text-decoration: underline !important;}
td[bgcolor='#ff0000'] a:hover {color: #fff !important; text-decoration: underline !important; font-weight: bold !important;}

/* Fugly background/no-shift > Biege to gray */
td[bgcolor='#f5f5dc'] {background-color: #dfdfdf !important; color: #999 !important;}

/* Black header > make softer */
table th, td[bgcolor='#000000'] {background-color: #030 !important; color: #ddd !important; border: 1px solid #696 !important; padding: 2px 5px !important;}

/* Table spacing/border cleanup */
form table {padding: 0px !important; border-spacing: 0px  !important; margin: 1em !important; border: 1px solid #222 !important;}
form table td {padding-left: 3px !important; padding-right: 3px !important; border: 1px solid #222 !important; text-align: center !important;}


}

@-moz-document url(https://scheduler.bebr.ufl.edu:8443/scheduler/viewShift.do), url(https://scheduler.bebr.ufl.edu:8443/scheduler/viewShift.do) {

/* Link hover appearance change on add-shift page */

#content form[name='ShiftDetailsForm'] table td a {width: 75px !important; height: 1.9em !important; font-weight: bold !important; color: #3d8f00 !important; padding: 3px 4px !important; text-decoration: underline !important;}
#content form[name='ShiftDetailsForm']  table td a:hover {font-weight: bold !important; color: #eee !important; background-color: #3d8f00 !important;}

/* row-hover highlighting */
#content form[name='ShiftDetailsForm'] table tr:hover {background-color: #c8ff9f !important;}

/* table padding fix for this page only */
table {padding: 0px !important; border-spacing: 0px  !important; margin: 1em !important;}
table td {padding-left: 3px !important; padding-right: 3px !important; text-align: center !important;}

}
