css:
@-moz-document domain("animeflv.net") {
.Header > .Mid {
    background: #252525 !important;
}
.Header > .Bot {
    background: #353535 !important;
}
.Wrapper > .Body {
    background: #202020 !important;
}
.Sidebar img {
    display: none !important;
}
.BX.Row.BFluid > .BFixed {
    padding-top: 0;
}
.Wdgt > .Top {
    background: #353535 !important;
    color: #eaeaea !important;
    border-bottom: 1px solid #01bcf3 !important;
}
.ScrlV {
    background: #353535 !important;
}
a,
.Title {
    color: #eaeaea;
}
.Anime.alt.B .Image {
    background: #01bcf3 !important;
}
.Anime.alt > a > .Title {
    color: #eaeaea;
}
.Title.Page h2 {
    color: #eaeaea !important;
}
.ListUser {
    background: #252525;
    border: 2px solid #01bcf3;
    border-radius: 4px;
    box-shadow: 0 0 7px #01bcf3;
}
.ListUser>li:last-child {
    border-color: #01bcf3
}
}

@-moz-document url-prefix("https://www3.animeflv.net/ver/") {
.CpCnA .CapiTop .Title {
    color: #eaeaea
}
.BFixed img {
    display: none !important;
}
.CpCnC {
    display: none;
}
.CpCnC .WdgtCn {
    display: none;
}

.CpCn {
	padding-right: 0;
}

.CapiTcn {
    height: 600px
}
.CpCnB .WdgtCn {
    background: #353535;
}
#layout {
    color: #fff !important;
}
.WdgtCn {
    box-shadow: none;
}
.fa-flag-o {
    background: #b34747 !important;
    color: #efefef;
}
.fa-flag-o:hover {
    background: #8a3434 !important;
    color: #adadad !important;
}
.BtnNw {
    background: #454545
}
.BtnNw.Xpnd {
    display: none !important;
}
}

@-moz-document url-prefix("https://www3.animeflv.net/anime/") {
.CpCnA .CapiTop .Title {
    color: #eaeaea
}
.CpCnC {
    display: none;
}
.CpCnC .WdgtCn {
    display: none;
}
.CpCnA {
    width: 1200px !important;
}
.CpCn {
    width: 1200px !important;
}
.CapOptns {
    width: 1200px !important;
}
.CapiTcn {
    height: 600px
}
.CpCnB .WdgtCn {
    background: #353535;
    width: 1200px;
}
#layout {
    color: #fff !important;
}
.WdgtCn {
    box-shadow: none;
}
.AnimeCover {
    background: #01bcf3
}
.Main .WdgtCn {
    background: #353535
}
.ListCaps > li {
    border-color: #01bcf3
}
.fa-heart, .fa-check, .fa-bookmark, .Sm {
    background: #353535;
    color: #aeaeae
}
.WdgtCn.Sm .Top {
    border-color: #01bcf3
}
.ListCaps > li .Stts {
    color: #aeaeae;
}
.ListCaps>li .Stts:after {
    background-color: #01bcf326;
}
.BtnNw {
    background: #353535;
    color: #aeaeae;
}
.ListAnmRel {
    border-color: #01bcf3;
}
.ListAnmRel > li > a {
    color: #efefef;
}
}

@-moz-document url-prefix("https://www3.animeflv.net/perfil/") {
.User {
    background: #01bcf3;
}
.WdgtCn {
    background: #353535;
}
.Anime.alt .Image {
    background-color: #01bcf3;
}
.Anime.alt > .Title > a {
    color: #efefef;
}
.Anime.alt > .Title > a:hover {
    color: #01bcf3
}
.btn-group > .btn {
    background: #008bb4;
    color: #fff;
}
.dropdown-menu {
    background: #282828;
    border: 2px solid #01bcf3 !important;
    box-shadow: 0 0 7px #01bcf3 !important;
}
.dropdown-menu>li>a {
    color: #ccc
}
.filters .multiselect-container>li>a>label:before {
    color: #414141;
}
.BX.Row.BFluid > .BFixed {
padding-top: 20px
}
}

@-moz-document url-prefix("https://www3.animeflv.net/browse") {
.btn-group > .btn {
    background: #008bb4;
    color: #fff;
}
.dropdown-menu {
    background: #282828;
    border: 2px solid #01bcf3 !important;
    box-shadow: 0 0 7px #01bcf3 !important;
}
.dropdown-menu>li>a {
    color: #ccc
}
.filters .multiselect-container>li>a>label:before {
    color: #414141;
}
.Title.Page h1 {
    color: #efefef
}
}
