css:
@-moz-document url-prefix("http://roblonium.com/"), url-prefix("http://roblonium.tk/"), url-prefix("http://sitetest1.roblonium.com/") {
#MasterContainer {
    background: white !important;
        background-position-x: 0%;
        background-position-y: 0%;
        background-repeat: repeat;
    background-repeat: repeat-x !important;
    background-position: 0% 20% !important;
    overflow: visible;
    width: 100%;
}
.BannerRedesign { 
    background:url(https://roblonium.com/images/RevisedHeader/bg-rbx_header.png) repeat-x !important;
}
#NavigationRedesignBannerContainer {
    background-image: url(https://roblonium.com/images/Themes/Holiday2013/snow.png) !important;
    background-repeat: no-repeat !important;
    background-position: bottom center !important;
}
#NavigationRedesignBannerContainer a.btn-logo,#NavigationRedesignBannerContainer a.btn-logo:visited {
    background:url(https://cdn.discordapp.com/attachments/493586785490829312/762412097706786847/holdiay.png) !important;
    width: 156px;
}
#NavigationRedesignBannerContainer a.btn-logo:hover 
{
    background:url(https://cdn.discordapp.com/attachments/493586785490829312/762412097706786847/holdiay.png) !important;
    background-position: 0 35px !important;
    width: 156px;
}
}

@-moz-document url-prefix("http://sitetest1.roblonium.com/") {
#Footer,
#MasterContainer,
#BodyWrapper {
    background: none !important;
}
#testingSitePanelWrapper {
    background: none !important
}
#NavigationRedesignBannerContainer.BannerCenterContainer a.btn-logo,
#NavigationRedesignBannerContainer.BannerCenterContainer a.btn-logo:visited,
#NavigationRedesignBannerContainer.BannerCenterContainer a.btn-logo:hover {
    background-image: url(https://cdn.discordapp.com/attachments/493586785490829312/762412097706786847/holdiay.png);
    width: 156px;
    margin-left: 0px;
}
#NavigationRedesignBannerContainer .HeaderDivider {
    border-left: 1px solid #878988
}
#Banner.BannerRedesign {
    background-image: url(http://roblonium.com/images/RevisedHeader/bg-rbx_header.png)
}
#Footer.unfixedFooter {
    background: transparent
}
#RepositionBody {
    background-color: transparent;
}
#Footer.LanguageRedesign div.legal {
    border-color: white;
}
#Footer.LanguageRedesign p.Legalese {
    color: #555 !important;
}
body {
    background-color: #fff !important;
}
body,
body.bodyUnfixedHeader {
    background:top center repeat-x #899fc1;
}
#SmallHeaderContainer div#Banner {
    background-color: #899fc1;
}
#Header .Navigation {
    background: url(http://s3.amazonaws.com/images.roblox.com/f73bca437c962753552fa7102e87ed07);
}
.Navigation li:hover {
    background: url(http://s3.amazonaws.com/images.roblox.com/f73bca437c962753552fa7102e87ed07);
    background-position: 0 30px;
}
}
