css:
@-moz-document domain("compresspng.com"), domain("compressjpeg.com"), domain("shrinkpdf.com"), domain("svgoptimizer.com"), domain("epstopng.com"), domain("epstosvg.com"), domain("svgtopdf.com"), domain("pdftosvg.com"), domain("svgtojpg.com"), domain("svgtopng.com"), domain("gifcompressor.com"), domain("giftomp4.com"), domain("giftopng.com"), domain("pngtogif.com"), domain("heic2jpg.com"), domain("jpg2png.com"), domain("png2jpg.com"), domain("webptojpeg.com"), domain("imagecompressor.com"), domain("pdf2doc.com"), domain("pdf2docx.com"), domain("pdftotext.com"), domain("pdftoimage.com"), domain("pdf2png.com"), domain("pdfextractor.com"), domain("pdfcompressor.com"), domain("combinepdf.com"), domain("jpg2pdf.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html
{
  background: #3d3d3f;
}

#main,
#format .ui-state-active, #format .ui-state-active:hover,

#switch .ui-state-active, #switch .ui-state-active:hover,

#pls a:hover
{
    background: #363639;
}

#format label,

#carousel-wrapper,

#switch label,

#pls a, #pls a.current:link, #pls a.current:visited, #pls a.current:active, #pls ul.dropdown
{
    background: #2e2e2f;
}

#carousel-prev-wrapper, #carousel-next-wrapper,

#carousel .ui-state-default,
.plupload_thumb_wrapper,

.plupload_file_progress, .plupload_file_button
{
    background: #12121354 !important;
}

body,

#like-it,

#format label,

.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default,

.plupload_file_status, .plupload_file_button,

#pls a
{
  color: #ccc !important;
}

*
{
  border: none !important;
  box-shadow: none !important;
}

clearImage 
{
    filter: invert(1) hue-rotate(180deg) saturate(2) brightness(.8);
    mix-blend-mode: color-dodge;
}
}
