css:
@-moz-document domain("erai-raws.info") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

body, html,

#wrapper
{
  background: #3d3d3f;
}

body
{
  background: #3d3d3f !important;
}

.page-link a:link, .page-numbers, .page-link a:visited, .page-numbers
{
    background: #363639;
}

nav, .bg-dark, .navbar-expand-md, .navbar
{
  /* Doesn't work, thanks Erai! */
  background: #363639 !important;
}

#container > div.col-12.col-sm-12.col-md-12.col-lg-3 > div.col-12.col-sm-12.col-md-4.col-lg-12 > div,
#container > div.col-12.col-sm-12.col-md-12.col-lg-3 > div.col-12.col-sm-12.col-md-8.col-lg-12 > div
{
    background: #2e2e2f !important;
}

.button5:hover, .release-table:hover,
.load_more_links:hover,

.ddmega:hover,

.page-numbers:hover,

.button, button
{
    background: #12121354 !important;
}

.label_primary_finale
{
    background: #ff2a2a;
}

body,

.page-numbers, .page-numbers:hover
{
  color: #ccc !important;
}

.aa_ss ,
.entry-title a:link, .entry-title a:visited,
.aa_ss_blue,

#site-info a,

.page-title span,

span[style*="#f00"], span[style*="#ff0000"]
{
    color: #ff8d8d!important;
}

*,

#main > div:nth-child(3)
{
  border-color: #0000 !important;
  box-shadow: none !important;
}

body,

#wrapper header
{
  padding: 0;
}

#container > div.col-12.col-sm-12.col-md-12.col-lg-3 > div.col-12.col-sm-12.col-md-4.col-lg-12 > div,
#container > div.col-12.col-sm-12.col-md-12.col-lg-3 > div.col-12.col-sm-12.col-md-8.col-lg-12 > div
{
  padding: 0 10px !important;
}

#wrapper
{
  margin: 0 auto;
}

#wrapper
{
  max-width: 100% !important;
  width: 100%;
}

#container
{
  margin: 0 auto;
  width: 70%;
}

#wrapper > div:nth-child(1) > header > div
{
  height: 250px;
  background: url("https://www.erai-raws.info/wp-content/uploads/2020/01/cropped-cropped-2020.jpg");
  background-position-y: 20%;
}
#wrapper > div:nth-child(1) > header > div img
{
  display: none;
}

.side_furo_2
{
  text-align: center;
  width: 100%;
  font-size: 19px;
}

clearImage 
{
    filter: invert(1) hue-rotate(180deg) saturate(2) brightness(.8);
    mix-blend-mode: color-dodge;
}
}
