css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com"){

ytd-search ytd-two-column-search-results-renderer {
    -webkit-justify-content: normal !important;
    justify-content: normal !important;
}

ytd-search ytd-two-column-search-results-renderer #primary.ytd-two-column-search-results-renderer {
    max-width: 862px !important;
}

ytd-search ytd-promoted-video-renderer ytd-thumbnail.ytd-promoted-video-renderer {
    max-width: 246px !important;
    width: 246px !important;
    height: 138px !important;
}/* koukoku */

ytd-search ytd-video-renderer ytd-thumbnail.ytd-video-renderer {
    max-width: 246px !important;
    width: 246px !important;
    height: 138px !important;
}/* douga */

ytd-search ytd-playlist-renderer ytd-playlist-thumbnail.ytd-playlist-renderer {
    max-width: 246px !important;
    width: 246px !important;
    height: 138px !important;
}/* playlist */

ytd-search ytd-radio-renderer ytd-thumbnail.ytd-radio-renderer {
    max-width: 246px !important;
    width: 246px !important;
    height: 138px !important;
}/* mixlist */

ytd-search ytd-channel-renderer #avatar-section.ytd-channel-renderer {
    max-width: 246px !important;
    width: 246px !important;
    height: 138px !important;
}/* channel */

ytd-search ytd-movie-renderer div.thumbnail-container.ytd-movie-renderer {
    max-width: 246px !important;
    width: 246px !important;
}/* movie */

ytd-search ytd-video-renderer.ytd-item-section-renderer:last-child{
    margin-bottom: 0px !important;
}/* section to iu 20ko kurai no douga gotoni arawareru sukima sakujo */

/*[[icon]]*/

}

style_setting_options.value:
ytd-search ytd-video-renderer #channel-info.ytd-video-renderer {
    padding: 0 0 !important;
}
ytd-search ytd-video-renderer ytd-video-meta-block{
    padding-left: 32px !important;
    min-height: 18px !important; /* karappo no toki ni kuzurenai you */
}
ytd-search ytd-video-renderer div#channel-info > a {
    top: -14px !important;
    position: relative !important;
}
ytd-search ytd-video-renderer div#channel-info ytd-channel-name{
        top: -6px !important;
    position: relative !important;
}
ytd-search ytd-video-renderer #description-text.ytd-video-renderer {
    margin-top: -6px !important;
}
ytd-search ytd-video-renderer #description-text.ytd-video-renderer {
    margin-bottom: 4px !important;
}

ytd-search ytd-video-renderer div#channel-info a yt-img-shadow{
    display:none !important;
}
ytd-search ytd-video-renderer div#channel-info.ytd-video-renderer{
    padding: 0px 0px 8px 0px !important;
}
ytd-search ytd-video-renderer div#channel-info ytd-channel-name{
    padding-left: 0px !important;
}
