css:
@-moz-document domain("sr.ht"), domain("sourcehut.org") {

body, h1, h2, h4, pre, .bold, .event, a.ref, .message-header details[open] > div, .navbar-brand, nav .brand a, header h1 a,
.nav-tabs .nav-link.active, .nav-tabs .nav-link.active:hover, .navbar-light .navbar-brand a, code,
input[type="email"], input[type="password"], input[type="tel"], input[type="text"],
textarea.form-control, .ticket-list .title > a, footer strong, footer h4, .infobox strong, .infobox h4 {
    color: rgb(238, 238, 238) !important;
}
.nav-tabs .nav-link {
    color: rgb(128, 128, 128) !important;
}
a, a.btn-link {
    color: rgb(117, 167, 242) !important;
}
.label a {
    color: inherit !important;
}
a.btn-default, .tag, a.btn  {
    color: rgb(16, 16, 16) !important;
}
body, header h1, html {
    background-color: rgb(16, 16, 16);
}
pre, .event, .nav-link.active, .message-header details[open] > div,
.footer, code, footer .box, .infobox .box, blockquote,
input[type="email"], input[type="password"], input[type="tel"], input[type="text"], textarea.form-control {
    background-color: rgb(32, 32, 32) !important;
}
.nav-link.active, .header-tabbed .nav .nav-item:hover, .header-extension {
    background-color: rgb(48, 48, 48) !important;
}
.label, .header-tabbed, .feedback {
    border-color: rgb(48, 48, 48);
}

}
