css:
@-moz-document url-prefix("https://www.mysdpbc.org/")
{
  .card
  {
    border-color: #ff1281 !important;
    animation: colorchange 60s;
    -webkit-animation: colorchange 60s;
    -webkit-animation-iteration-count: infinite;
  }

    @keyframes colorchange
  {
    0%   {border-color: red;
  }

  0%
  {
    border-color: yellow;
  }

  50%
  {
    border-color: blue;
  }

  10%
  {
    border-color: green;
  }

  70%
  {
    border-color: red;
  }
}

.navbar-institution
{
  background: #ffc4df;
}

body
{
  color: #A1A1A1;
}

.login-card .card-body
{
  padding: 0.325rem;
  background-color: #ffa1c2;
}

.login-card .card-img-top
{
  border-bottom: 1px solid #ffc4df !important;
}

.SSOShortcutDiv > .card.login-card:hover .card-body, .SSOShortcutDiv > .card.login-card:hover .card-body > .card-text
{
  background-color: #ff1e7c;
  background-color: #ff1e7c;
}

.container-fluid
{
  background-color: #ffc9e2;
}

.row
{
  background-color: #ffc9e2;
}

.SSOShortcutContainerDiv > .col > .card.container-card > .card-body
{
  background-color: #ffc9e2;
}

#tb_shortcutGlobalSearch
{
  background-color: #ff8fbc;
  color: #000;
  border: 1px solid #fff;
}

.institution-masthead-image
{
  height: 250px;
  background-image: url(https://www.sanrio.com/media/W1siZiIsIjIwMjAvMDEvMTQvMjAvMzMvMTEvODY4L01NX01vYmlsZV9XYWxscGFwZXJfMDMuanBnIl1d/MM_Mobile_Wallpaper_03.jpg?sha=69b57d274745020d);
}

.loginheader
{
  background: #ffa3da;
}

.PortalPageContainer
{
  background-color: #ffc9e2!important;
  border-top: 1px solid #ffffff!important;
}

.form
{
  background: #ff8cde;
}

.formlinks
{
  background: #ffa3da;
  color: #ffffff;
}

.icon-input i
{
  color: #000;
}

a
{
  color: #FFFFFF;
  text-decoration: none;
}

element.style
{
  color: #FFFFFF;
}

body
{
  color: #ffcfe3;
  width: auto;
  background: url(https://hdwallpaper20.com/wp-content/uploads/2017/07/My-Melody-wallpaper-2.jpg);
  height: auto;
  background-position: center;
  background-size: cover;
  margin: auto;
  padding: 0;
  background-attachment: fixed;
  background-repeat: no-repeat;
}

*
{
  color: #fff;
}

.text-center:after
{
  border: 1px solid #ff1f8b !important;
  border: 0px solid #ff6ead !important;
  animation: colorchange 60s;
  -webkit-animation: color2 60s;
  -webkit-animation-iteration-count: infinite;
}

@keyframes color2
{
  0%
  {
    color: red;
  }

  25%
  {
    color: yellow;
  }

  50%
  {
    color: blue;
  }

  10%
  {
    color: green;
  }

  10%
  {
    color: red;
  }
}

.PortalPageContainer .bg-white, .PortalPageContainer footer .bg-light
{
  background-color: #ffc9e2!important;
  border-top: 1px solid #ffc9e2!important;
}

.SSOShortcutContainerDiv > .col > .card.container-card > .card-header
{
  background-color: #ffe3f1;
  color: #fff;
}

.dropdown-menu
{
  color: #ffbde1;
  background-color: #ffe3f1;
  border: 1px solid rgb(255, 255, 255);
}

.thead-default th
{
  color: #fff;
  background-color: #ffc4df;
}

.navbar-institution .navbar-nav
{
  background: #ff73ad;
}

.navbar-institution .navbar-nav .nav-link
{
  color: #ffffff;
}

.navbar > .container, .navbar > .container-fluid
{
  background-color: #ffe3f2;
}

.text, .form-group .text, .form-group .text:focus, .text:focus, .input-group.group .input-group-addon, select
{
  box-shadow: -11px 9px 0 0 rgba(0,0,0,.1);
}

button, input, optgroup, select, textarea
{
  color: white;
}

.btn
{
  background: #ffd9e9;
}
}

@-moz-document url-prefix("https://sis.palmbeachschools.org/focus/")
{
  .site-content-container
  {
    background: rgb(58, 58, 58);
  }

  .BoxContent, .BoxContentBackground
  {
    background-color: #ffbddb;
  }

  .site-menu-sidebar .site-menu
  {
    background: rgb(34, 34, 34) !important;
  }

  .WhiteDrawHeader
  {
    background: #ff91ce;
  }

  .site-inactive-year .site-header
  {
    background: rgb(34, 34, 34) !important;
    color: #ff91ce !important;
  }

  .site-menu-sidebar .site-menu-group-title
  {
    color: rgb(225, 225, 225);
  }

  .LO_header
  {
    border-bottom: 1px solid #ffebf4;
    border-left: 1px solid #ffebf4;
    border-top: 0px solid #ffebf4;
    background: #ffa1c6 repeat-x scroll left top;
  }

  .site-inactive-year , .custom-background-color, .site-inactive-year, .site-inactive-year, .site-inactive-year
  {
    background-color: rgb(34, 34, 34) !important;
  }

  .custom-border-color, .before-custom-border-color:before, .after-custom-border-color:after
  {
    border-color: rgb(255, 0, 0) !important;
    border-right: 1px solid rgb(255, 0, 0);
    animation: color3 60s;
    -webkit-animation-iteration-count: infinite;
  }

    @keyframes color3
  {
    0%   {border-color: red;
  }

  25%
  {
    border-color: yellow;
  }

  50%
  {
    border-color: blue;
  }

  10%
  {
    border-color: green;
  }

  10%
  {
    border-color: red;
  }
}

.custom-border-color
{
  background: #ff9cd6;
}

B, TD
{
  color: rgb(255, 255, 255);
}

B, TD
{
  color: rgb(230, 230, 230);
}

.BoxContents
{
  background: #ff9cd6;
}

select
{
  -webkit-writing-mode: horizontal-tb !important;
  background-color: rgb(40, 40, 40);
}

.site-menu-sidebar .site-header
{
  border-bottom: 1px solid rgb(65, 65, 65);
  background: rgb(17, 17, 17);
}

DIV.listoutputbox
{
  background: #f00060;
  box-sizing: initial;
}

button, html input[type=button], input[type=reset], input[type=submit]
{
  color: #ff5c8d;
}

.EntireBox
{
  background: #f0bbd8;
}

.LO_field
{
  height: 26px;
  font-size: 12px;
  text-decoration: none;
  border-left: 1px solid #D7D7D7;
}

.left-header img
{
  max-height: 35px;
  background: #000000;
}

.left-header img
{
  max-height: 35px;
  background: #00000000;
}

.site-footer-middle:after
{
  border: 1px solid #ff006f !important;
  border: 0px solid #ff00aa !important;
  animation: colorchange 60s;
  -webkit-animation: colorchange 60s;
  -webkit-animation-iteration-count: 99999999999999999999;
}

@keyframes colorchange
{
  0%
  {
    color: red;
  }

  0%
  {
    color: yellow;
  }

  5%
  {
    color: blue;
  }

  0%
  {
    color: green;
  }

  10%
  {
    color: red;
  }
}

.PortalPageContainer .bg-white, .PortalPageContainer footer .bg-light
{
  background-color: #393939!important;
  border-top: 1px solid #393939!important;
}

.SSOShortcutContainerDiv > .col > .card.container-card > .card-header
{
  background-color: #ffe8f4;
  color: #fff;
}

.dropdown-menu
{
  color: #ff99d1;
  background-color: #ffe8f4;
  border: 1px solid rgb(255, 255, 255);
}

site-header-icon-image
{
  color: #ffbfdc;
  width: auto;
  background: url(https://hdwallpaper20.com/wp-content/uploads/2017/07/My-Melody-wallpaper-2.jpg);
}

body
{
  line-height: 1.3;
}

site-header-icon-image:after
{
  background: url(https://hdwallpaper20.com/wp-content/uploads/2017/07/My-Melody-wallpaper-2.jpg);
}

.LO_field
{
  background-color: #ff8cc0;
}

.LO_header
{
  border-bottom: 1px solid #ff3098;
  border-left: 1px solid #ff3098;
  border-top: 0px solid #ff3098;
  background: #ff3098 repeat-x scroll left top;
  color: #ffffff;
}
}

@-moz-document url-prefix("https://www.mysdpbc.org/SSO/Portal/LaunchApplication")
{
  body
  {
    color: #ff0080;
    width: auto;
    background: url(https://svgshare.com/i/ET5.svg);
    height: -webkit-fill-available;
    background-position: center;
    background-size: 100px;
    margin: auto;
    padding: 0;
    background-repeat: no-repeat;
    background-color: #ffd9ec;
  }

  .app-auth-animation video, .app-auth-animation
  {
    display: block;
    text-indent: -9999px;
    width: 0px;
    height: 0px;
    background: url(https://svgshare.com/i/ET5.svg);
    background-size: 100px 82px;
  }
}
