css:
@-moz-document url-prefix("http://acm.xidian.edu.cn") {
body{
    background-image:none;
    background-color:#fcfcfc;
}

/*解除wrapper限制*/
div#wrapper{
    left:0%;
    width:100%;
}

div#head{
    position:static;
    width:8%;
    float:left;
    text-align:left;
    margin-left:20px;
}

div#menu.navbar{
    height:50;
    background: #fff none no-repeat fixed top;
    box-shadow: 0px 0px 3px #888888;
    /*position:fixed;*/
}

div#menu>a.btn{
    display:block;
    float:left;
    padding: 15 25 15 25;
    margin-top:0!important;
    
    font-family: "'微软雅黑','Helvetica Neue',Helvetica,Arial,sans-serif";
    font-size:15px!important;
    font-weight: normal;
    
    border: none;
    border-radius: 0px;
    
    background-color: #fff!important;
    background-image: none;
    cursor: pointer;
}

div#menu>a.btn:hover{
    background-color: #008877!important;
    color:#fff;
    box-shadow: 0px 3px 3px #004433;
}

div#menu>a.btn.btn-warning{
    background-color: #008877!important;
}

span#dict_status.btn{
    visibility:hidden;
}

div#head>h2>span.red{
    display:none;
}

marquee{
    display:none;
}

div#broadcast.container{
    display:none;
}


div.area_toolbar{
    display:none;
}

span#EditAreaArroundInfos_source{
    display:none;
}

select#language{
    margin:10px;
}

textarea#source{
    margin:15px;
    border-radius:0px;
}

textarea#source:focus{
    box-shadow:none;
    border-color:#00ccaa;
}

input#submit{
    background-image:none;
    font-size: 20px;
    padding: 10px 20px 10px 20px;
    background-color:#008877;
    border-radius:0px;
}

input#submit:hover{
    background-color:#004433;
}

input.btn.btn-danger{
    display:none;
}
}

@-moz-document domain("acm.xidian.edu.cn") {
div#main{
    margin-left:7%;
    width:86%;
}

a{
    color: #000;
}

big>b{
    font-size:28px;
    color:#008877;
}

big b:before{
    content : "▶ ";
    margin-left:-30;
}

td>p{
    margin-top:15px;
    font-size:15px;
    line-height:28px;
    font-family: "'微软雅黑','Helvetica Neue',Helvetica,Arial,sans-serif";
}

ul>li{
    line-height:22px;
}
}

@-moz-document url-prefix("http://acm.xidian.edu.cn/problemset.php"), url-prefix("http://acm.xidian.edu.cn/status.php"), url-prefix("http://acm.xidian.edu.cn/ranklist.php"), url-prefix("http://acm.xidian.edu.cn/discuss/discuss.php"), url-prefix("http://acm.xidian.edu.cn/contest.php") {
tr.toprow{
    background-color:#008877;
    background-image: none;
    height:50px;
    /*box-shadow: 0px 0px 5px #00ccaa;*/
}

table#problemset,table.table-striped.content-box-header{
    box-shadow: 0px 0px 8px #aaaaaa;
}

th.header:hover{
    background-color:#006655;
}

th.header.headerSortUp:after{
    content:" ▲";
}

th.header.headerSortDown:after{
    content:" ▼";
}

div.btn.btn-success,div.btn.btn-danger,
span.btn.btn-success,span.btn.btn-danger,span.btn.btn-warning,
a.btn.btn-warning
{
    background-image:none!important;
    border : 0px;
    border-radius:3px;
}

a.btn.btn-warning{
    text-shadow:none;
    color:white!important;
}

table#problemset td,table#problemset th,
table.table-striped.content-box-header th,
table.table-striped.content-box-header td
{
    vertical-align:middle;
}

table#problemset tr.evenrow,table#problemset tr.oddrow{
    background-color:#fff;
    height:50px;
}

form.form-search{
    font-size:20px;
    height:30px;
}

input.input-small.search-query,input.input-large.search-query{
    border-radius:0px;
    font-size:20px;
    padding-top:15px;
    padding-left:5px;
    padding-bottom:15px;
    border:none;
    box-shadow:none;
    border-bottom : 1px solid #6699cc;
    height:30px;
}

input.input-small.search-query:focus,input.input-large.search-query:focus{
    border-bottom : 2px solid #6699cc
}

div.left a{
    background-color: #eee;
    padding-top: 8px;
    padding-bottom: 8px;
    padding-left: 20px;
    padding-right: 20px;
    top:100px;
    border-radius:3px;
}

div.left a:hover{
    background-color: #bbb;
    color:#fff;
    text-decoration: none;
    box-shadow: 0px 0px 3px #888;
}

div#main>h3>span.red{
    border-radius:3px;
    background-color: #008877;
    padding:5px 10px 5px 10px;
    color:#fff;
}

div#main>h3>a{
    border-radius:3px;
    background-color: #aaa;
    padding:5px 10px 5px 10px;
    color:#fff;
}

button.btn.btn-mini{
    padding:8px 10px 8px 10px;
    border-radius:3px;
    background-color:#008877;
    background-image:none;
    font-size:15px;
    text-shadow:none;
    color:#fff;
}

button.btn.btn-mini:hover{
    background-color:#006655;
    background-image:none;
    font-size:15px;
    text-shadow:none;
    color:#fff;
}

table#result-tab a{
    color:#6699cc;
}
}

@-moz-document url-prefix("http://acm.xidian.edu.cn/problem.php?") {
div.content{
    padding:10px 20px 4px;
    margin: 10px 0px 10px 0px;
    background-color:#fff;
    box-shadow: 0px 0px 5px #ccc;
    font-family: "'微软雅黑','Helvetica Neue',Helvetica,Arial,sans-serif";
}

div.content>p,div.content>div{
    font-size:18px;
    line-height:28px;
}

pre.content {
    box-shadow: 0px 0px 5px #ccc;
}

.content:hover{
    background-color:#eee;
}

div#main>h2{
    color:#0088cc;
}

span.sampledata{
    background-color:rgba(0,0,0,0);
}

}

@-moz-document url-prefix("http://acm.xidian.edu.cn/status.php") {

}

@-moz-document url("http://acm.xidian.edu.cn/contest.php") {
tbody:last-child td:last-child{
    display:none;
}

table{
    margin-top:15px;
    margin-bottom:15px;
}

tr.evenrow,tr.oddrow{
    font-family: "'微软雅黑','Helvetica Neue',Helvetica,Arial,sans-serif";
    height:40px;
    text-align:center;
}

}

@-moz-document url-prefix("http://acm.xidian.edu.cn/ranklist.php") {
table{
    margin-top:15px;
    margin-bottom:15px;
}

tr.evenrow,tr.oddrow{
    font-family: "'微软雅黑','Helvetica Neue',Helvetica,Arial,sans-serif";
    height:40px;
    text-align:center;
}

tbody tr:first-child td:first-child{
    color:white;
    background-color:gold;
    box-shadow: 0px 0px 5px gold;
}

tbody tr:nth-child(2) td:first-child{
    color:white;
    background-color:silver;
}

tbody tr:nth-child(3) td:first-child{
    color:white;
    background-color:brown;
}

td:nth-child(2) div.center>a{
    color:#0088cc;
}

tr.toprow,tbody{
    box-shadow: 0px 0px 10px #aaa;
}

input[name="user"]{
    border-radius:0px;
    font-size:20px;
    padding-top:15px;
    padding-left:5px;
    padding-bottom:15px;
    border:none;
    box-shadow:none;
    border-bottom : 1px solid #6699cc;
    height:30px;
}

input:focus[name="user"]{
    box-shadow:none;
    border-bottom : 2px solid #6699cc
}

input[type="submit"]{
    font-size:15px;
    padding: 5px 10px 5px 10px;
    border-radius:3px;
    background-color:#008877;
    color:#fff;
}

input[type="submit"]:hover{
    background-color:#004433;
}
}
