css:
@-moz-document domain("9minecraft.net"), domain("mc-mod.net") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, .6);
             background-image: url('https://static.wikia.nocookie.net/minecraft_gamepedia/images/5/58/On_Respawn_Anchor_%28top_texture%29_JE1_BE1.png/revision/latest/scale-to-width-down/16?cb=20200922011957');
			background-repeat: repeat;
        }

body {
	background-image: url('https://i.pinimg.com/originals/26/6d/f7/266df7ab6322509273a9cddeeee5a3e7.jpg');
	background-size: 64px;
	background-repeat: repeat;
	color: #bbb;
	background-color: #392a1c;
}
#page .container-fluid {
	background: #0000009c;
}
a {
    color: #808eb1;
	text-shadow: 2px 1px 2px #000;
}
.date_time {
	color: #a3a3a3;
}
.postContent {
	color: #fff;
}
.postTitle_content h1, .postTitle h2 {
    color: #0072cd;
	text-shadow: 2px 1px 2px #000;
}
#lsidebar .widget ul, #rsidebar .widget ul, #rsidebar li li, #lsidebar li li {
	filter: invert(100);
}
#lsidebar h2, #rsidebar h2 {
	border-color: #3f3f3f;
}
.mine-green a {
    color: #268bbd!important;
}
.mine-red a {
    color: #991228!important;
}
.archiveTitle h1 {
	color: #991228;
}
.gsc-control-cse {
	background-image: url('https://i.pinimg.com/originals/a5/ae/11/a5ae11de9f6bfa0376e2421ff0bc890f.jpg');
	background-size: 64px;
	background-repeat: repeat;
	border-color: #303030;
}
input#gsc-i-id1 {
	filter: invert(100) !important;
	background-color: #0000 !important;
}
.gsc-input-box {
	/*background-image: url('https://i0.wp.com/www.craftycreations.net/wp-content/uploads/2019/08/Stone-Block-e1566147610895.png?fit=500%2C500&ssl=1');*/
	background-size: 64px;
	background-repeat: repeat;
	border-color: #303030;
	background-color: #0000008c;
	backdrop-filter: blur(1px);
}
input[type=text], input[type=email], input[type=url], input[type=password], input[type=search], input[type=tel], input[type=number], textarea {
	filter: inherit !important;
	color: #000 !important;
}
.postContent h3 {
    color: #246fcc;
	text-shadow: 2px 1px 2px #000;
}
#dynamic-to-top {
	border-color: #000;
}
.postContent a {
    color: #00a6ff;
	text-shadow: 2px 1px 2px #000;
}
.postContent blockquote, .gs-no-results-result .gs-snippet, .gs-error-result .gs-snippet {
	background-image: url('https://i.pinimg.com/originals/52/ae/1d/52ae1d1a17f72c634f8b15549ed3959c.jpg');
	background-size: 64px;
	background-repeat: repeat;
	border-color: #a4a476;
}
.post_bookmark_2 {
	background: #0000;
}
.posttags a {
	background: #65b32e;
	color: #1d1d1d;
	text-shadow: 1px 1px 2px #0000;
}
.posttags a::before {
    background: #392a1c;
    box-shadow: inset 0 1px rgba(255, 255, 255, .25);
}
.posttags a::after {
	background: #392a1c;
	border-left-color: #65b32e;
}
.gsc-completion-selected, .gsc-option-menu-item-highlighted {
	background-image: url('https://i.pinimg.com/originals/a5/ae/11/a5ae11de9f6bfa0376e2421ff0bc890f.jpg');
	background-size: 64px;
	background-repeat: repeat;
	color: #fff;
	text-shadow: 1px 1px 2px #000;
}
.gsc-completion-container {
	background-image: url('https://i.pinimg.com/originals/26/6d/f7/266df7ab6322509273a9cddeeee5a3e7.jpg');
	background-size: 64px;
	background-repeat: repeat;
	border-color: #392a1c;
}
.gssb_a, .gssb_a td {
	color: #fff;
	text-shadow: 2px 1px 2px #000;
}
.gssb_a {
	background: #00000070;
}
[src='https://www.google.com/cse/static/images/1x/en/branding.png'] {
	filter: invert(100);
}
.postTitle_archive a {
    color: #006fc8;
	text-shadow: 2px 1px 2px #000;
}
.list-post {
	background-color: #1b19692e;
}
.list-post .item {
	background-color: #0000;
	border-color: #2d3f8847;
}
.archiveTitle {
	border-color: #5c442d;
}
.gsc-webResult.gsc-result, .gsc-results .gsc-imageResult, .gsc-webResult.gsc-result:hover {
	background: #0000;
}
.gsc-above-wrapper-area {
	border-color: #666;
}
.gsc-webResult.gsc-result, .gsc-webResult.gsc-result:hover {
	border-color: #0000;
}
.gcsc-find-more-on-google {
    color: #0092ff;
	text-shadow: 2px 1px 2px #000;
}
.gcsc-find-more-on-google-magnifier {
	fill: #0092ff;
	filter: drop-shadow(2px 1px 2px #000);
}
.gsc-tabHeader.gsc-tabhInactive, .gsc-refinementHeader.gsc-refinementhInactive {
	background: #0000;
	color: #fff;
	text-shadow: 2px 1px 2px #000;
	border-color: #666;
}
.gsc-tabsArea {
	border-color: #999;
}
.gsc-tabHeader.gsc-tabhActive, .gsc-refinementHeader.gsc-refinementhActive {
	background: #0000;
	text-shadow: 2px 1px 2px #000;
	color: #1aa7e8;
}
.gsc-orderby-label {
	color: #eee;
	text-shadow: 2px 1px 2px #000;
}
.gsc-selected-option-container {
	background: #0a0a0a;
	color: #bbb;
	box-shadow: 0 1px 1px rgba(255, 255, 255, .1);
	border-color: rgba(255, 255, 255, .1);
}
.gsc-control-cse .gsc-option-menu {
	background-image: url('https://i.pinimg.com/originals/26/6d/f7/266df7ab6322509273a9cddeeee5a3e7.jpg');
	background-size: 64px;
	background-repeat: repeat;
	border-color: rgba(255, 255, 255, .2);
	box-shadow: 0 2px 4px rgba(255, 255, 255, .2);
}
.gsc-option-menu-item {
	color: #bfbfbf;
	text-shadow: 2px 1px 2px #000;
}
.gsc-option-menu-item-highlighted {
	color: #fff;
}
.gsc-wrapper {
	background: #000000cf;
	border-radius: 10px;
	padding: 2px;
}
.gsc-result-info {
	color: #bfbfbf;
	text-shadow: 2px 1px 2px #000;
}
.gs-result .gs-image, .gs-result .gs-promotion-image {
	border-color: #121212;
}
[src='https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTyo0OfTiYllDOG3M57IDgGvw2ZWw6yD15Jitv4a5OEQvwneQDRfmLlHBc'] {
	filter: invert(100);
	border-color: #e2e2e2 !important;
}
.gs-webResult:not(.gs-no-results-result):not(.gs-error-result) .gs-snippet, .gs-fileFormatType {
	color: #ccc;
}
.gsc-results .gsc-cursor-box .gsc-cursor-page {
	background: #0000;
	color: #999;
	border-color: #999;
}
.gsc-results .gsc-cursor-box .gsc-cursor-current-page {
    border-color: #1A73E8;
    color: #1A73E8;
}
.gs-webResult.gs-result a.gs-title:hover, .gs-webResult.gs-result a.gs-title:hover b, .gs-imageResult a.gs-title:hover, .gs-imageResult a.gs-title:hover b, .gs-webResult.gs-result a.gs-title:visited, .gs-webResult.gs-result a.gs-title:visited b, .gs-imageResult a.gs-title:visited, .gs-imageResult a.gs-title:visited b, .gs-webResult.gs-result a.gs-title:link, .gs-webResult.gs-result a.gs-title:link b, .gs-imageResult a.gs-title:link, .gs-imageResult a.gs-title:link b {
    color: #1181cc;
}
.postContent blockquote a {
	color: #f00;
	text-shadow: 2px 1px 2px #fff;
}
blockquote > p {
	color: #000;
	text-shadow: 2px 1px 2px #fff;
}
}

@-moz-document url-prefix("http://www.9minecraft.net/sitemap_index.xml"), url-prefix("http://www.mc-mod.net/sitemap_index.xml"), url-prefix("http://www.mc-mod.net/post-sitemap"), url-prefix("http://www.mc-mod.net/page-sitemap"), url-prefix("http://www.mc-mod.net/category-sitemap"), url-prefix("http://www.mc-mod.net/post_tag-sitemap"), url-prefix("http://www.mc-mod.net/mine_version-sitemap"), url-prefix("http://www.mc-mod.net/author-sitemap"), url-prefix("http://www.9minecraft.net/post-sitemap"), url-prefix("http://www.9minecraft.net/page-sitemap"), url-prefix("http://www.9minecraft.net/category-sitemap"), url-prefix("http://www.9minecraft.net/post_tag-sitemap"), url-prefix("http://www.9minecraft.net/author-sitemap") {
#content {
	background: #000000b3;
	border-radius: 10px;
	padding: 8px;
}
#sitemap tr:nth-child(odd) td {
	background: #111111b3 !important;
}
#sitemap tbody tr:hover td, #sitemap tbody tr:hover td a {
	color: #fff;
}
#sitemap tbody tr:hover td {
	background: #333333b3;
}
a:visited {
	color: #888;
}
}

@-moz-document domain("mc-mod.net") {
#lsidebar .widget ul, #rsidebar .widget ul, #rsidebar li li, #lsidebar li li {
	filter: inherit;
}
.mine-green a {
    color: #AFFFA4 !important;
}
.titlecategory a {
    color: #3c9dd2;
}
}
