css:
@-moz-document domain("skeb.jp") {
body {
    background-color: rgba(16, 16, 16, 1);
}


/* SKEB */
.logo {
  font-size   : 120%;
  color       : rgb(52, 69, 69, 1) !important;
  text-shadow:  2px  2px 2px rgba(180, 0, 0, 0.2),
               -2px  2px 2px rgba(180, 0, 0, 0.2),
                2px -2px 2px rgba(180, 0, 0, 0.2),
               -2px -2px 2px rgba(180, 0, 0, 0.2)
}
.logo img {
    -webkit-filter: drop-shadow(0 0 6px rgba(210, 95, 95, 0.5)) !important;
}


/* ヘッダーとフッダー */
.navbar,
.footer{
    background-color: rgba(8, 14, 14, 0.9)!important;
}
.navbar-link,
a.navbar-item,
.is-pulled-right
{
        color: rgba(240, 248, 255, 1);
}
.navbar-link,
a.navbar-item:hover,
.is-pulled-right
{
        color: rgba(240, 248, 255, 1);
        background-color: rgba(32, 16, 16, 0.9) !important;
}


/* ヘッダーの検索バー */
.navbar-link {
    background-color: rgba(8, 14, 14, 0.9)!important;
}
.navbar-dropdown {
    background-color: rgba(8, 14, 14, 1);
}
.subtitle {
    color: rgba(240, 248, 255, 1);
}
.svg-inline--fa {
    font-size   : 110%;
}
.control.has-icons-left,
.input {
    font-weight: bold;
}


/* 「トップページ」「お知らせ」「リクエストページ」等の共通部 */
.title,
strong {
  font-size   : 120%;
    color:rgba(240, 248, 255, 1);
}

.panel {
    font-size: 140%;
    font-weight: bold;
}

.is-box {
    background-color: rgba(32, 32, 32, 1)!important;
    color: rgba(240, 248, 255, 1);
}

.is-nsfw {
    background-color: rgba(32, 16, 16, 0.9) !important;
    color: aliceblue;
}

.tag:not(body).is-turquoise,
.tag:not(body).is-green,
.tag:not(body).is-rounded {
    font-size: 120% !important;
    font-weight: bold;

}
.tags.has-addons .tag:not(:last-child),
.tags.has-addons .tag:not(:first-child),
.tag:not(body).is-pink,
.tag:not(body).is-info {
    font-size: 110% !important;
    font-weight: bold;
}
.field.is-grouped.is-grouped-multiline {
    width: 515px;
}

.is-m-l-0{
    margin-left: 15px !important;
}

.button.is-link {
    font-size: 110% !important;
    font-weight: bold;
    padding: 20px 40px;
    bottom: 10px;
}
.panel-block{
    background-color: rgba(48, 48, 48, 1)!important;
    color: rgba(240, 248, 255, 1);
}
.panel-block:hover {
    background-color: rgba(48, 36, 36, 1)!important;
    color: rgba(240, 248, 255, 1);
}
.tag:not(body).is-dark{
    background-color: rgba(0, 0, 0, 0.7)!important;
    color: rgba(240, 248, 255, 1);
}


/* 「アカウントページ」 */
.label:not(:last-child) {
    color: rgba(240, 248, 255, 1);
    font-weight: bold;
}


/* 「ユーザーページ」 */
.hero-foot,
.input.is-static {
    background-color: rgba(32, 32, 32, 1)!important;
    color: rgba(240, 248, 255, 1);
    font-weight: bold;
}
.level-left > .level-item > .subtitle, .level-left > .level-item > .title {
    font-size   : 200%;
    font-weight: bold;
}


/* 「新期リクエストページ」 */
.field-label .label {
    color: rgba(240, 248, 255, 1);
}


/* 「お知らせ」のメッセージ部 */
.message-body {
    background-color: rgba(4, 4, 4, 1)!important;
    color: rgba(240, 248, 255, 1);
}


/* 「ユーザーページ」や「作品ページ」の詳細テーブル */
.table tbody {
    background-color: rgba(32, 32, 32, 1);
    color: rgba(240, 248, 255, 1);
    font-weight: bold;
}


/* クライアントおよびクリエイターリンクボックス */
.card-content {
    background-color: rgba(16, 16, 16, 0.9);
    color: rgba(240, 248, 255, 1);
    font-weight: bold;
}


/* 「納品」のポップアップ */
.modal-card {
display: inline-block;
box-shadow: 0px 0px 5px 5px rgba(120, 255, 255, 0.3);
}
.modal-card-foot, .modal-card-head {
    background-color: rgba(32, 32, 32, 1);
    color: rgba(240, 248, 255, 1);
    font-weight: bold;
}
.is-80 {
    width: 110px !important;
}
.modal-card-body,
.checkbox{
    background-color: rgba(16, 16, 16, 0.9);
    color: rgba(240, 248, 255, 1);
    font-weight: bold;
}
.checkbox:hover {
    background-color: rgba(32, 16, 16, 0.9);
    color: rgba(240, 248, 255, 1);
    font-weight: bold;
}


/* リンクカラー */
a,
.breadcrumb a{
    color: rgba(48, 152, 182, 1);
    font-weight: bold;
}
a:hover,
.breadcrumb a:hover{
    color: rgba(48, 152, 182, 1) ;
    font-weight: bold;
    text-shadow:  2px  2px 1px rgba(153, 208, 94, 0.3),
                 -2px  2px 1px rgba(153, 208, 94, 0.2),
                  2px -2px 1px rgba(153, 208, 94, 0.3),
                 -2px -2px 1px rgba(153, 208, 94, 0.2)
}


/* テキスト入力欄 */
.textarea {
    color: rgba(240, 248, 255, 1);
    font-weight: bold;
}
}
