@-moz-document domain("bitbucket.org") {
/* View the original style by mauriciogior here: https://userstyles.org/styles/181130/bitbucket-dark-mode */
/* My modifications were made to fix some text highlighting and color contrast issues that I found. */

h1, h2, h3, h4, h5, p, a, li, div, b, input, textarea, pre, label
{
    color: #f5f5f5 !important;
}

ins {
    color: #b8e994 !important;
}

/* i really want this to be global */
body, #page, [data-testid="ContextualNavigation"] > div
{
    background-color: #22222D;
}

[class*=sc-]:not([role="menu"]),
[data-testid="Navigation"] > div:nth-child(2),
[data-testid="Navigation"] > div:nth-child(2) * {
    background-color: transparent !important;
}

[class*=css-], [class*=css-] > div, [data-testid="NavigationItem"] > div
{
    background-color: #22222D !important;
}

[class*=HelpBadge],
#helpGlobalItem span,
[class*=sc-],
[class*=sc-] * {
    border-color: #7289DA !important;
}

[class*=FieldTextStateless] * {
    background-color: #22222D !important;
    border-color: #2B2B36 !important;
}

[class*=Item], [class*=Item] * {
    background-color: #2B2B36 !important;
    color: #fff !important;
}

[data-role="droplistContent"] {
    border: 1px solid white;
}

[role="presentation"]:hover, [data-testid="NavigationItem"]:hover > div {
    background-color: #2B2B36 !important;
}

[role="presentation"]:hover * {
    background-color: transparent !important;
}

[class*=ItemGroup__GroupTitle] {
    color: #7289DA !important;
}

#content, #content *, .codehilite pre, pre {
    background-color: #2B2B36 !important;
}

#changeset-diff, #changeset-diff *, #changeset-diff *:before, #changeset-diff *:after {
    border-color: #7289DA !important;
}

.select, textarea {
    border: 1px solid #7289DA !important;
}

textarea {
    background-color: #2B2B36 !important;
}

[id*=select], [id*=select] *, [class*=select], [class*=select] * {
    color: #000 !important;
}

.aui-popup, .aui-popup [class*=dialog] {
    background-color: #2B2B36 !important;
}

.field-group label {
    color: #f5f5f5 !important;
}

.field-group .field-value * {
    color: #7289DA !important;
}

.css-1q7i31j code {
    color:black;
}

.css-wyjj6g {
    color: white;
}

}
