css:
@-moz-document domain("offcloud.com") {
/* Main Web */
 ::-webkit-scrollbar {
    width: 6px;
    background-color: #222;
}
::-webkit-scrollbar-thumb {
    background-color: #0093f1;
}
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
}
.masthead, .footercta {
    background-image:unset;
    background-color: rgb(17 17 17);
}
.menu {
    background-color: #333;
}
.list-menu > li, .list-menu.list-login {
    border-top: 1px solid rgba(0, 0, 0, .1);
}
.form-loginbg, .highlight, .wrapper .portfolio .item-holder .item .overlay-background {
    background-color: rgb(17 17 17);
}
.form-loginbg:before {
    background-color: unset;
}
.form-login .btn-submitlogin {
    color:#444;
}
.form-join .input-email, .showcase, .path, .feat-picto, .img-logos, .img-torrent img, .accepted-payments, #tableList tr, .form, .screenshot, img[src*="icon-"], .btnout, .illustration, img[src*="learn.offcloud.com/wp-content/uploads/"], canvas {
    filter: invert(93.5%) hue-rotate(180deg);
}
.persistant-menu {
    color:#000;
}
.btn-play {
    border: 1px solid #222;
}
.btn-play:hover, .section-title h2 a, .tutorials h4, strong, b, .wrapper .portfolio .item-holder .item .overlay .inner-overlay h3, .wrapper .portfolio .meta-tags-holder .selector .meta-tags-outside h3 {
    color:#ccc;
}
    
.thumbnail, .testblock, .pricing.color1 {
    background-color: rgb(0 0 0 / 0.25);
}
.testblock:after {
    color:#222;
}
.clientblock p strong {
    color: #adbcc1;
}
.list-features li, .article .body p {
    color: #94a3a8;
}
h6, .persistant-menu .list-inline a:hover, .article h1, .article h2 {
    color:#eee;
}
.list-featblocks .circle {
    border: 1px solid rgb(0 0 0 / 0.1);
    color: rgb(0 154 255 / 0.75);
}
.list-featblocks > li p, .article .body .list-featblocks > li p, .wrapper .project-single .single-info .project-description p {
    color: #999;
}
.wrapper .page-title .heading-title, .wrapper .page-title h1, .wrapper .portfolio .filters span {
    color:#787878;
}
.heading-title p a {
    color:#337ab7;
}
.heading-title p a:hover, .heading-title p a:focus {
    color:#0093f1;
}
.wrapper .portfolio .filters ul li.active, .wrapper .portfolio .filters ul li:hover {
    color:#ccc;
    background-color:#2a2a2a;
}
footer .footer-widgets {
    border-bottom: 1px solid #333;
}
footer .footer-copyright p, footer .footer-copyright ul li a {
    color:#777;
}
.wrapper .project-single a {
    color:#777;
}
.wrapper .project-single a:hover, .wrapper .project-single a:focus {
    color:#3b3b3b;
}
.wrapper header nav ul li {
	padding: 10px 10px;
}
/* After Login */
 #header.navbar .navbar-toolbar, .sidebar .topmenu, .sidebar, .modal-header, .persistant-menu {
    background-color:#000;
}
.sidebar .topmenu li.active {
    background-color: rgb(255 255 255 / 0.07);
}
.jumbotron>.info {
    background-color: rgb(0 0 0 / 0.9);
}
.row, .timeline .header, body, .yellow, .testmonials {
    background-color: #111;
}
.timeline:after, .submit {
    background-color: #0093f1;
}
.panel {
    border-color: #222;
    border-bottom-color: #222;
    background-color: #2a2a2a;
}
.panel-footer {
    background-color: #2a2a2a;
    border-top: 1px solid #222;
    color: #999;
}
.form-control {
    color: #ddd;
    background-color: #444;
    border: 1px solid #333;
}
.btn-default {
    color: #ddd;
    background-color: #444;
    border-color: #333;
    text-shadow: 0 1px 0 rgb(255 255 255 / 0.5);
}
.panel.panel-minimal, .sidebar, .sidebar .content>.heading, .dropdown-menu>li>a, .close, .page-header .title {
    color:#ccc;
}
.media-list>.media>.media-body span.media-meta, .media-list>.media>.media-body p.media-meta, .media-list>.media>.media-body small.media-meta {
    color:#aaa;
}
#header.navbar .navbar-toolbar .navbar-nav>li.open>a {
    background-color: #000;
    color: #008de7;
}
#header.navbar .navbar-toolbar .navbar-nav>li>a {
    color:#008de7
}
#header.navbar .navbar-toolbar .navbar-nav>li.open>a:hover, #header.navbar .navbar-toolbar .navbar-nav>li.open>a:focus, #header.navbar .navbar-toolbar .navbar-nav>li.open>a:active {
    background-color: rgba(255, 255, 255, .15);
}
#header.navbar .navbar-toolbar .navbar-nav>.dropdown>.dropdown-menu {
    background-color: #111;
    border: 1px solid #2a2a2a;
    color:#ccc;
}
.dropdown-menu>li.active>a, .dropdown-menu>li .active>a:hover {
    background-color:#fbfbfb;
    color:#444
}
.panel hr {
    border-color: #444;
}
.media-list>.media:after {
    border-bottom: 1px solid #444;
}
.timeline .header.year {
    background: #222;
    color:#ccc;
}
.timeline .events>.wrapper>.figure {
    box-shadow: 0 0 0 3px rgb(0 0 0 / 0.15);
}
.bgcolor-success {
    background-color: #008be4!important;
    color: #333!important;
    border-color: rgba(0, 0, 0, .1)!important;
}
.timeline .events>.wrapper:nth-of-type(odd)>.panel:after, .timeline .events>.wrapper:nth-of-type(even)>.panel:before {
    border-color: transparent transparent transparent #777;
}
.list-table {
    color:#ddd;
}
hr, .panel-group .panel-heading+.panel-collapse .panel-body {
    border-top: 1px solid #444;
}
.text-muted, .text-default {
    color:#ccc!important;
}
.progress .progress-bar.progress-bar-success {
    background-color: #019cff;
}
.btn-success {
    text-shadow: 0 -1px 0 rgba(0, 0, 0, .1);
    background-color: #0093f1;
    border-color:#333;
}
.btn-success:hover, .submit:hover {
    background-color: #019cff;
    border-color: #0093f1;
}
.modal-content, .modal-body {
    background-color:#111;
}
.modal-footer {
    border-top: 1px solid #000;
}
.modal-body h4, .table, .panel-body h4 {
    color:#ccc;
}
.modal-body p {
    color:#aaa;
}
.panel-default>.panel-heading {
    color: #fff;
    background-color: #019cff;
    border-color: rgba(0, 0, 0, .1);
}
.panel>.panel-body+.table, .panel>.panel-body+.table-responsive, .table>thead>tr>th, .table>tbody>tr>th, .table>tfoot>tr>th, .table>thead>tr>td, .table>tbody>tr>td, .table>tfoot>tr>td, .control-label {
    color:#ccc;
    border-top: 1px solid #333!important;
}
.panel-body {
    color:#fff;
}
.widget .panel, .widget.panel {
    border-bottom-color: #222;
}
h4, .h4, h5 b, h3, .h3, h5, .flot-tick-label .tickLabel, h2, h2 a, h1 {
    color:#ccc;
}
.jumbotron>.info, .page-header.page-header-block {
    background-color: rgb(0 0 0 / 0.65);
}
.list-group-tabs>.list-group-item>a:hover, .list-group-tabs>.list-group-item>a:focus, .list-group-tabs>.list-group-item.active>a {
    background-color:#999;
    color:#000;
}
.list-group-tabs>.list-group-item {
    background-color:#0f0f0f;
}
.form-horizontal.form-bordered .form-group, .form-horizontal.form-striped .form-group {
    border-top-color: #444!important;
}
.bgcolor-default {
    background-color: #2a2a2a!important;
    color: #444!important;
    border-color: rgba(0, 0, 0, .1)!important;
}
.form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control, .dropdown-menu .divider {
    color:#444;
}
.panel-toolbar .ng-binding {
    color:#777;
}
.page-header.page-header-block {
    color:#333!important;
}
.timeline .events>.wrapper:nth-of-type(odd)>.panel:before {
    border-color: transparent transparent transparent #111;
}
.timeline .events>.wrapper:nth-of-type(odd)>.panel:after, .timeline .events>.wrapper:nth-of-type(even)>.panel:before, .timeline .events>.wrapper:nth-of-type(even)>.panel:after {
    border-color: transparent #2a2a2a;
}
.dropdown-menu {
    border-color: #222;
    background-color: #2a2a2a;
}
.table>thead>tr>th, .table tr>th {
    background-color: #2a2a2a;
}
/* Blog */
 .nav-responsive, footer.page-footer, .post-list, .pagination {
    color:#000;
    background-color:#000;
    background: #000;
}
.nav-responsive .show-nav {
    color:#777;
}
.nav-responsive {
    margin-bottom:unset;
}
#menu-main-1, .post-item {
    background-color:#000;
}
.nav-responsive .lower hr {
    background:#222;
}
.nav-responsive li a {
    color:#777;
}
.nav-responsive li a:hover, .nav-responsive li a:focus, .pagination .next, .pagination .previous, .widget h3, .widget.latestposts h5 a, .pagination ul.page-numbers li a, .pagination ul.page-numbers li span {
    color:#ccc;
}
.button.gray-outline {
    color: #aaa;
    border: 2px solid #2d3031;
}
.button.light-gray {
    color: #eee;
    border: 2px solid #2d3031;
    background: #00a1ff;
}
.widget.socialshare .sharebutton.twitter, .widget.socialshare .sharebutton.facebook, .widget.socialshare .sharebutton.reddit, .widget.socialshare .sharebutton.pinterest, .widget.socialshare .sharebutton.linkedin, .widget.socialshare .sharebutton.google, .author-profile {
    background:#1c1c1c;
}
.pagination ul.page-numbers li a:hover {
    color:#eee;
    background:#303434
}
.pagination .previous:before, .pagination ul.page-numbers li a, .pagination ul.page-numbers li span {
    background:unset;
}
.sidebar-right {
    padding:25px;
}
.widget.latestposts .meta .date a, .post-share li span, .post-share li a {
    color:#aaa;
}
.widget.latestposts h5 a:hover {
    color:#fff
}
.widget {
    border-bottom: 1px solid #222;
}
.post-list hr, .post-item-header hr {
    background:#222;
}
footer.page-footer {
    margin-top:unset;
}
.sidebar-right {
    height: unset;
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
}
.pagination, .post-squares, .author-profile {
    margin:unset;
}
.searchform .query {
    color: #ccc;
    border: 1px solid #222;
    background: #333;
}
.post-share {
    margin:0;
    background: rgb(0 0 0 / 0.5);
}
.post-share li span:after, .post-share li a:after {
    background:#333;
}
}
