css:
@-moz-document domain("1337x.to"), domain("1337x.st"), domain("x1337x.ws"), domain("x1337x.eu"), domain("x1337x.se"), domain("1337x.is")
{
  .table-striped > tbody > tr td,
.list-box ul,
.featured-heading,
.box-info-detail,
div.box-info > div:nth-child(2),
.tab-nav li.active a,
.filter-all li a
  {
    background: #212121;
  }

  .table-striped > tbody > tr:nth-of-type(2n+1) td,
div.fancy-select div.trigger,
div.fancy-select ul.options,
div.fancy-select select:focus + div.trigger.open,
#tracker-list li,
.recent-episodes li,
.filter div.fancy-select div.trigger,
.filter,
.filter-all,
nav li:hover a
  {
    background: #1a1a1a;
  }

  .table-list td.name .comments,
.table-list th,
.box-info .box-info-heading,
.user-box li,
.banner-box li span,
.torrent-tabs .tab-nav,
.comment-info .detail,
.comment-detail .frame.user,
.pagination li a,
.torrent-work .torrent-work-detail,
#tracker-list h3,
nav li.active a,
.pagination li a
  {
    background: #111;
  }

  .table-list td a,
.table-list td,
.list-box ul a,
.table-list td.coll-1 .icon i,
.box-info-detail p,
.box-info-detail p a,
.table-list td.name .comments,
.table-list th,
.box-info-time,
.box-info-time i,
.trending-torrent-explore .box-info.trending h1,
.trending-torrent .featured-heading h1,
.banner-box li span,
.tab-nav li a,
.comment-info .detail p,
.box-info .tab-content h2,
.comment-info .detail .user-name a,
.file-content li,
.pagination li a,
div.fancy-select div.trigger,
div.fancy-select ul.options,
.search-page .box-info .box-info-heading h1 span,
.torrent-work .torrent-work-detail,
#tracker-list h3,
#tracker-list li,
.recent-episodes.box-info .box-info-heading h1,
.recent-episodes .torrent-detail-info p,
.recent-episodes .torrent-detail-info h3 a,
.filter label,
.filter div.fancy-select div.trigger,
.filter-all li a,
.featured-heading,
.tab-content > .active > p
  {
    color: #999;
  }

  .table-list td.coll-1,
.table-list td.coll-2,
.table-list td.coll-3,
.table-list td.coll-date,
.table-list td.coll-4,
.list-box li,
.table-list td.name .comments,
.comment-info .detail,
.box-info .box-info-heading,
.torrent-tabs .tab-nav,
.torrent-work .torrent-work-detail,
#tracker-list li,
#tracker-list li:last-child,
.recent-episodes li,
.filter div.fancy-select div.trigger,
.recent-episodes .torrent-detail .torrent-image-wrap
  {
    border-color: #000;
  }

  .torrent-tabs .tab-nav li,
div.fancy-select ul.options,
div.fancy-select div.trigger,
.filter-all li a
  {
    border-color: #222;
  }

  .divider
  {
    filter: invert(100%);
  }

  .comment-info .detail .user-name a
  {
    text-shadow: none;
  }

  .filter,
.filter-all
  {
    border: 1px solid #222;
  }

  .form-control,
.form-control:focus,
.sceditor-container iframe,
.sceditor-container textarea,
.post-comment .comment-box .detail textarea
  {
    background-color: #151515;
    border: 1px solid #212121;
    color: white;
  }

  .form-control:focus
  {
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgb(214, 54, 0);
  }

  .account-settings .form-row
  {
    border-top: 1px solid #0f0f0f;
  }

  .tab-nav li a
  {
    border-bottom: 3px solid #d63600;
  }

  .tab-nav li.active a
  {
    background-color: #3c3c3c;
    border-bottom: 3px solid #6a8616;
  }

  .table-list td.name .comments,
.table-list th,
.box-info .box-info-heading,
.user-box li,
.banner-box li span,
.torrent-tabs .tab-nav,
.comment-info .detail,
.comment-detail .frame.user,
.torrent-work .torrent-work-detail,
#tracker-list h3,
.sceditor-container,
div.sceditor-toolbar,
.featured-heading
  {
    background: #111c;
  }

  .trending-torrent-explore .box-info.filter-list,
.series-library-detail .table-list tr.blank
  {
    background-color: #0000 !important;
  }

  .banner-box li
  {
    border-color: #222c;
  }

  .rule-list li:first-child,
.box-info .tab-content h2
  {
    border-top: 1px solid #0f0f0f;
  }

  .rule-list li,
.upload-form .form-row,
.box-info .tab-content h2
  {
    border-bottom: 1px solid #0f0f0f;
  }

  .box-info .box-info-heading.bordered,
.featured-heading,
.trending-torrent-explore .box-info.trending .box-info-heading
  {
    border-bottom: 4px solid #7c2a0c;
  }

  .sceditor-container
  {
    border: 1px solid #11111180;
    border-top-color: #7c2a0c;
  }

  .sceditor-button:hover,
.sceditor-button:active,
.sceditor-button.active
  {
    background: #962e08;
    color: #FFF;
  }

  .sceditor-button
  {
    border-right: 1px solid #962e08;
    border-bottom: 1px solid #962e08;
  }

  .comment-box .detail::after
  {
    border-right: 8px solid #000;
  }

  #comments .pagination a,
#comments .pagination span
  {
    background-color: #952800;
    color: #999;
  }

  .series-library-detail .table-list tr
  {
    background-color: #212121;
  }

  .series-library-detail .table-list-wrap
  {
    background-color: #151515;
  }

  .table-bordered > tbody > tr > th,
.table-bordered > tfoot > tr > th,
.table-bordered > tbody > tr > td,
.table-bordered > tfoot > tr > td
  {
    border: 1px solid #d63600;
  }

  .series-library-detail .table-list tr.blank td
  {
    border-top: 1px solid #0000;
    border-bottom: 1px solid #0000;
  }

  .series-library-detail .box-info-detail
  {
    background-color: #222c;
  }

  .torrent-category-detail,
.black-box
  {
    background: #181818;
  }

  .btn.cccdabbc
  {
    box-shadow: 0 5px 0 rgb(42, 42, 42);
  }

  .tab-nav li:hover a,
.tab-nav li.active:hover a
  {
    background-color: #515151;
  }

  a[href*="spy"],a[href*="vpn"],
.download-links-dontblock li,
.afdddfbb /**,
div.box-info > div:nth-child(2) > div:nth-child(1) > ul:nth-child(1) > li:nth-child(7),
div.box-info > div:nth-child(2) > div:nth-child(1) > ul:nth-child(1) > li:nth-child(9),
div.box-info > div:nth-child(2) > div:nth-child(1) > ul:nth-child(1) > li:nth-child(13),
div.box-info > div:nth-child(2) > div:nth-child(1) > ul:nth-child(1) > li:nth-child(15),
div.box-info > div:nth-child(2) > div:nth-child(1) > ul:nth-child(1) > li:nth-child(16),
div.box-info > div:nth-child(2) > div:nth-child(1) > ul:nth-child(1) > li:nth-child(10),
div.box-info > div:nth-child(2) > div:nth-child(1) > ul:nth-child(1) > li:nth-child(11),
div.box-info > div:nth-child(2) > div:nth-child(1) > ul:nth-child(1) > li:nth-child(19),
div.box-info > div:nth-child(2) > div:nth-child(1) > ul:nth-child(1) > li:nth-child(22),
div.box-info > div:nth-child(2) > div:nth-child(10),
div.box-info > div:nth-child(2) > div:nth-child(11),
div.box-info > div:nth-child(2) > div:nth-child(12),
div.box-info > div:nth-child(2) > div:nth-child(13),
div.box-info > div:nth-child(2) > div:nth-child(14),
div.box-info > div:nth-child(2) > div:nth-child(15),
div.box-info > div:nth-child(2) > div:nth-child(16),
div.box-info > div:nth-child(2) > div:nth-child(17),
div.box-info > div:nth-child(2) > div:nth-child(18),
div.box-info > div:nth-child(2) > div:nth-child(19) **/
  {
    display: none;
  }

  .download-links-dontblock li:nth-child(1)
  {
    display: inherit;
  }

  .torrent-detail
  {
    background: #0e0e0e;
  }

/* body {
  filter: blur(.1px);
} */

  .torrent-detail-info p
  {
    font-family: opensans regular,sans-serif;
    font-size: 14px;
  }
}
