@-moz-document url-prefix("https://www.youtube.com/") {
#avatar.ytd-rich-grid-media {display: none !important;}#video-title.ytd-rich-grid-media{font-size: 1.4rem !important;line-height: 1.6rem !important}
#avatar-link.ytd-rich-grid-media{margin-right:0 !important;}
#dismissable #avatar-link{display: none !important}#dismissable #details #video-title.ytd-rich-grid-media{font-size: 1.4rem !important;line-height: 1.6rem !important}#title.ytd-rich-shelf-renderer{color: var(--yt-spec-text-primary);font-size: 1.6rem}ytd-video-meta-block[rich-meta] #channel-name.ytd-video-meta-block,ytd-video-meta-block[rich-meta] #metadata-line.ytd-video-meta-block{font-size: 1.3rem !important}#rich-shelf-header.ytd-rich-shelf-renderer{margin: 14px 0 14px 0px !important}ytd-rich-shelf-renderer{border-top: 1px solid var(--yt-spec-10-percent-layer)}#show-more-button.ytd-rich-shelf-renderer{border-bottom: 1px solid var(--yt-spec-10-percent-layer)}ytd-rich-shelf-renderer[is-show-more-hidden] #dismissable.ytd-rich-shelf-renderer{border-bottom: 1px solid var(--yt-spec-10-percent-layer)}#contents.ytd-rich-shelf-renderer ytd-rich-item-renderer.ytd-rich-shelf-renderer{margin-bottom: 24px}ytd-rich-item-renderer{margin-left: calc(4px / 2);margin-right: calc(4px / 2);margin-bottom: 24px;width: calc(100% / var(--ytd-rich-grid-items-per-row) - 4px - 0.01px)}ytd-rich-section-renderer{width: 100%;margin-left: calc(4px / 2);margin-right: calc(4px / 2)}#grid-header.ytd-rich-grid-renderer{margin: 24px 0 24px 4px}h3.ytd-rich-grid-video-renderer{margin: 8px 0 8px!important}ytd-primetime-promo-renderer.ytd-rich-section-renderer,ytd-inline-survey-renderer.ytd-rich-section-renderer{border-top: 1px solid var(--yt-spec-10-percent-layer);border-bottom: 1px solid var(--yt-spec-10-percent-layer)}/*and: skeleton-loading*/.rich-grid-media-skeleton {margin-left: calc(4px / 2) !important;margin-right: calc(4px / 2) !important;margin-bottom: 24px !important;}.rich-grid-media-skeleton {flex-basis: var(--ytd-rich-grid-item-max-width) !important;min-width: var(--ytd-rich-grid-item-max-width) !important;max-width: var(--ytd-rich-grid-item-max-width) !important;}#home-page-skeleton .channel-avatar {display: none;}#home-page-skeleton .text-shell {height: 16px !important;}/*end: skeleton-loading*/html:not(.style-scope){--ytd-rich-grid-item-max-width: /*[[size]]*/;--ytd-rich-grid-item-margin: 4px !important;}#contents.ytd-rich-grid-renderer, #home-container-media, #grid-header.ytd-rich-grid-renderer{width: calc(100% + 4px + 0.01px) !important;max-width: calc( var(--ytd-rich-grid-items-per-row) * (var(--ytd-rich-grid-item-max-width) + var(--ytd-rich-grid-item-margin))) !important}@media (max-width:456px){#home-container-skeleton, #home-page-skeleton, #home-container-media, ytd-rich-grid-renderer{--ytd-rich-grid-items-per-row: 1 !important;--ytd-rich-grid-posts-per-row: 1 !important}}@media (min-width:457px){#home-container-skeleton, #home-page-skeleton, #home-container-media, ytd-rich-grid-renderer{--ytd-rich-grid-items-per-row: 2 !important;--ytd-rich-grid-posts-per-row: 2 !important}}@media (min-width:671px){#home-container-skeleton, #home-page-skeleton, #home-container-media, ytd-rich-grid-renderer{--ytd-rich-grid-items-per-row: 3 !important;--ytd-rich-grid-posts-per-row: 3 !important}}@media screen and (min-width: 957px){#home-container-skeleton, #home-page-skeleton, #home-container-media, ytd-rich-grid-renderer.style-scope.ytd-two-column-browse-results-renderer{--ytd-rich-grid-items-per-row: 4 !important;--ytd-rich-grid-posts-per-row: 2 !important;--ytd-rich-grid-movies-per-row: 5 !important}}@media screen and (min-width: 1171px){#home-container-skeleton, #home-page-skeleton, #home-container-media, ytd-rich-grid-renderer.style-scope.ytd-two-column-browse-results-renderer{--ytd-rich-grid-items-per-row: 5 !important;--ytd-rich-grid-posts-per-row: 3 !important;--ytd-rich-grid-movies-per-row: 6 !important}}@media screen and (min-width: 1600px){#home-container-skeleton, #home-page-skeleton, #home-container-media, ytd-rich-grid-renderer.style-scope.ytd-two-column-browse-results-renderer{--ytd-rich-grid-items-per-row: 6 !important;--ytd-rich-grid-posts-per-row: 3 !important;--ytd-rich-grid-movies-per-row: 9 !important}}#home-container-skeleton, #home-page-skeleton, #home-container-media, ytd-rich-grid-renderer.style-scope.ytd-two-column-browse-results-renderer{--ytd-rich-grid-items-per-row: /*[[quantity]]*/;}

/*Размер - Size*/
html:not(.style-scope) {
    --ytd-rich-grid-item-max-width: /*[[sizes]]*/; /*250px !important*/
}

/*Строки - Lines*/
#home-container-skeleton, #home-page-skeleton, #home-container-media, ytd-rich-grid-renderer.style-scope.ytd-two-column-browse-results-renderer {
    --ytd-rich-grid-items-per-row: /*[[quan]]*/; /*8 !important*/
}
/*Размер - Size*/
html:not(.style-scope) {
    --ytd-rich-grid-item-max-width: auto; /*250px !important*/
}

/*Строки - Lines*/
#home-container-skeleton, #home-page-skeleton, #home-container-media, ytd-rich-grid-renderer.style-scope.ytd-two-column-browse-results-renderer {
    --ytd-rich-grid-items-per-row: auto; /*8 !important*/
}


#home-page-skeleton {justify-content: center;align-items: center;}}
