css:
@-moz-document domain("travis-ci.org") {
#left, .loading-page, .main, .wrapper {
  background-color: #222;
}

.jobs-item {
  border-color: #444;
}

a, body, button, html {
  color: #ddd;
}

.job-number {
  border-color: #999;
}

.job-name::after, .job-lang::after, .job-env::after {
  background: linear-gradient(-90deg,#222 0,rgba(34,34,34, 0) 100%);
}

.jobs-item:active, .jobs-item:hover {
  background-color: #444 !important;
}

.jobs-item:hover .job-env:hover, .jobs-item:hover .job-lang:hover, .jobs-item:hover .job-name:hover {
  background-color: #444 !important;
}

.jobs-item:hover .job-env::after, .jobs-item:hover .job-lang::after, .jobs-item:hover .job-name::after {
  background: linear-gradient(-90deg,#444 0,rgba(0,0,0,0) 100%) !important;
}

.build-header {
  background: transparent !important;
}

.build-commit::after {
  background: linear-gradient(-90deg,#222 20%,rgba(34,34,34,0) 100%);
}


.build-header.passed {
  border-color: #555;
}

.repo-navigation {
  border-color: #222;
}

pre.codedisplay[class*="language-"] code {
  color: #999;
}

pre.codedisplay[class*="language-"] {
  background-color: #222 !important;
}

code[class*="language-"], pre[class*="language-"] {
  text-shadow: none;
}

.token.boolean, .token.constant, .token.deleted, .token.number, .token.property, .token.symbol, .token.tag {
  color: #f09;
}

.row-li {
    border-color: #444;
}
.row-li.passed {
    background: linear-gradient(to right,#39aa56 0,#39aa56 10px,#222 10px,#222 100%) no-repeat;
}
.row-li.booting, .row-li.created, .row-li.queued, .row-li.received, .row-li.started {
    background: linear-gradient(to right,#edde3f 0,#edde3f 10px,#222 10px,#222 100%) no-repeat;
}
.row-li.errored, .row-li.failed {
    background: linear-gradient(to right,#db4545 0,#db4545 10px,#222 10px,#222 100%) no-repeat;
}
.tile.passed {
    background: linear-gradient(to right,#39aa56 0,#39aa56 8px,#222 8px,#222 100%) no-repeat;
}
.tile.booting, .tile.created, .tile.queued, .tile.received, .tile.started {
    background: linear-gradient(to right,#edde3f 0,#edde3f 8px,#222 8px,#222 100%) no-repeat;
}
.tile.errored, .tile.failed {
    background: linear-gradient(to right,#db4545 0,#db4545 8px,#222 8px,#222 100%) no-repeat;
}

.pr-row .row-branch:hover a {
    background: transparent !important;
}
.pr-row .row-branch::after, .pr-row .row-message::after {
    background: linear-gradient(-90deg,#222 0,rgba(0,0,0,0) 100%);
}
.fade-out::after {
    background: linear-gradient(-90deg,#222 30%,rgba(0,0,0,0) 100%);
}
.branch-row .row-commiter::after {
    background: linear-gradient(-90deg,#222 20%,#fff0 100%);
}

.build-tiles > .passed {
    background-color: #343;
}
.build-tiles > .failed {
    background-color: #433;
}
.build-tiles > .started {
    background-color: #443;
}


.build-tiles li {
    border-color: #222;
    background-color: #333;
}


.sidebar-list ul {
  background-color: #222;
  border-color: #222;
}

.tile h2::after {
  background: linear-gradient(-90deg,#222 0,rgba(34,34,34,0) 100%);
}


#left {
  border-right: 2px solid #444;
}

.travistab-nav--secondary a, .travistab-nav.travistab-nav--underline {
  border-bottom: 2px solid #222;
}

.search--sidebar .search-inner {
    border-color: #666;
}
.search--sidebar .search-inner input {
    color: #fff;
    background: transparent;
}
.footer a {
    color: #555;
}

button {
    background: transparent;
}
.action-button--cancel, .action-button--debug, .action-button--prioritize, .action-button--restart, .option-display, .option-tofu {
    border-color: #444;
}

}
