css:
@-moz-document url-prefix(https://psprices.com/)
{

@media (min-width: 1200px) {
body { background: transparent; }
.bg-info { background: #e5e5e5 !important; }
.retail--deals, .game_card__like, .game_card__hide { display: none; } 
.content__game_card__cover {  margin-bottom: 5px; }
.content__game_card__discount { font-size: 20px; font-weight: 600 !important; }
.content__game_card .discount--val { color: red; font-size: 16px; font-weight: bold !important; }
.content__game_card__end_date { font-size: 12px; margin-top: 4px; }
.col-xl-2 { flex: 0 0 25%; max-width: 25%; } 
.game-card--image { max-height: 100%; margin: 0 -10px; width: calc(100% + 20px); }
.content__game_card { padding: 0 10px 10px 10px; border: 1px solid rgba(0,0,0,.12); background: rgba(0,0,0,.02); border-radius: 6px; min-height: 375px; }
.old_price { float: right; }
.content__game_card__title { height: 37px; }
.content__game_card__title .title { display: block; color: #000; font-size: 15px; height: 20px; overflow: hidden; font-weight: bold; white-space: nowrap;text-overflow: ellipsis; }
.content__game_card .discount--val { background: #fff; font-size: 17px; padding: 3px 5px 3px; right: -19px; top: 6px; -webkit-box-shadow: 0px 0px 3px 3px rgba(0,0,0,0.15); -moz-box-shadow: 0px 0px 3px 3px rgba(0,0,0,0.15); box-shadow: 0px 0px 3px 3px rgba(0,0,0,0.15); }
.game_card__buttons { position: absolute; display: block; top: 9px; left: 9px; z-index: 9; }
.game_card__button { padding-top: 0; padding-bottom: 0; font-size: 17px; padding: 2px 10px; border: 0; background: #fff; -webkit-box-shadow: 0px 0px 5px 3px rgba(0,0,0,0.25); -moz-box-shadow: 0px 0px 5px 3px rgba(0,0,0,0.25); box-shadow: 0px 0px 5px 3px rgba(0,0,0,0.25); }
.game_card__button.active { background: red; }
}
}
