css:
@-moz-document url-prefix("https://optifine.net/") {
body {
    background-image: url(data:image/png+xml;base64,);
  	color: white;
}
.tableRoot {
    background-color: #222;
    box-shadow: 0px 0px 20px #424242;
    color: white;
}
h1 {
    color: #fff;
}
::-webkit-scrollbar {
    width: 10px;
    height: 10px;
}
::-webkit-scrollbar-thumb {
    background: #171717;
}
::-webkit-scrollbar-thumb:hover {
    background: #1c1c1c;
}
::-webkit-scrollbar-thumb:active {
    background: #202020;
}
::-webkit-scrollbar-track {
    background: rgb(6, 6, 21);
}
}

@-moz-document url-prefix("https://optifine.net/downloads") {
.downloadLinePreview {
    background-color: #313131;
}
.downloadLineMain:first-child {
    background-color: #2b2b2b;
}
.downloadLineMore {
    background-color: #282828;
}
}

@-moz-document url-prefix("https://optifine.net/donationCapeEdit") {
.tableData {
    background-color: #2b2b2b;
}
TD .tableDataEditable {
    background-color: #00000000;
}
}
