css:
@-moz-document url("http://fci.utm.edu.ec:88/evirtual/"), url-prefix("http://fci.utm.edu.ec:88/evirtual/") {
:root {
    --color0: white;
   /*  
    Así estaba al comienzo :v
    1 bajo
    2 intermedio
    3 fuerte
    --color1: #312d2d;
    --color2: #1f1d1d;
    --color3: #050505;
    */
    
    /*VARIABLES GRÁFICAS*/
    --color1:  #1f1d1d;
    --color2:  #312d2d;
    --color3:  #1f1d1d;
    
	/*COLORES TAREAS*/ 
    --neutro: rgb(202, 126, 11);
    --positivo: rgb(48, 122, 77);
    --negativo: rgb(131, 43, 43);
}



.container-fluid, .frontpage-guest-header{
    background-color:var(--color1) !important;	
}

body {
    background-color:var(--color1) !important;
}
nav, .fixed-top {
    background-color: var(--color2)!important;
}
.navbar .nav-item a {
    color: var(--color0)!important;
}
#nav-drawer {
    background:var(--color2)!important;
    border-top: solid 2px var(--color1);
}
#nav-drawer-footer {
    background:var(--color3) !important;
}
.drawer-toggle {
    background:var(--color3) !important;
}
.card-body, .card-footer {
    background:var(--color2);
    color:var(--color0);
}
a {
    text-decoration:none !important;
}
span, label, b, .section li, a, p, i, td, th, h1, h2, h3, h4, h5 {
    color:var(--color0) !important;
}
.collapse, .collapsing {
    background:var(--color2) !important;
}
.list-group-item:hover {
    background:var(--color3) !important;
}
.dropdown-menu, .dropdown, .show, .text-username {
    background-color: var(--color2) !important;
}
.dropdown-menu a:hover{
    background:var(--color3) !important;
}



/*, .carousel-inner*/
 .carusel, .slide {
    display: block;
    height:25em;
    width: 100%;
    background: rgba(1, 8, 1, 0.5) !important;
}
img {
    opacity:100;
    filter:alpha(opacity=-600);
    /* For IE8 and earlier */
}

/*-------------------------------  VENTANAS DE DIALOGO --------------------------------*/
div[role="dialog"] {
	background: var(--color2) !important;
}

.moodle-dialogue-wrap {
    border: none !important;
	background: var(--color2) !important;
    border: solid 1px var(--color0) !important;
}

.moodle-dialogue-hd {
	background: var(--color3) !important;
}

/**/
.confirmation-message{
	color: var(--color0);
}

.modal-content{
	background: var(--color2);
}

#page{
    background: var(--color3) !important;
}

.form{
    background: var(--color2) !important;
}

.row{
	background: var(--color3);
}

/*---------------------------- BOTONES -------------------------*/
input[type="button"], .btn-primary{
	background: var(--color3) !important;
	border: solid 1px var(--color0) !important;
}

section .btn{
	background: var(--color3) !important;
    color: var(--color0) !important;
	border: none!important;
}

/*------------------------------   SECCIÓN CURSOS  ------------------------------------*/
.list-group ul{
    background-color: var(--color2) !important;
    border-right: solid 1px var(--color3);
} 

.tab_initial {
    color: var(--color0);
}
.breadcrumb a, .breadcrumb span, ul .active{
    background-color: var(--color3) !important;
}

.active span, .active .tab_initial {
    color:var(--color0) !important;
}

.activity div{
    color: var(--color0) !important;
}

.mod-indent-outer li{
	    color: var(--color0);
}

.availabilityinfo {
	color: var(--color0);
}

.isrestricted {
	color: var(--color0);	
}

.isrestricted span{
	background: var(--negativo);
    font-weight: 200;
}

/*------------------------------   SECCIÓN TAREAS  ------------------------------------*/
 .c1 {
    background-color: var(--color03)!important;
    color:var(--color0) !important;
}
/*.submissionstatussubmitted{
    border-bottom: solid 1px black;
}
*/
 .earlysubmission, .submissionstatussubmitted {
    background-color: var(--positivo) !important;
}
.latesubmission, .submissionstatusnotsubmitted {
    background-color: var(--negativo) !important;
}
.submissionnotgraded {
    background-color: var(--neutro) !important;
}
.submissiongraded {
    background-color: var(--positivo) !important;
}


/*----------------------  SECCIÓN USUARIOS EN LINEA Y ACTIVIDAD RECIENTE ----------------*/

.hidden-print{
	background: var(--color1);
}



/*---------------------- PÁGINA LOGIN ------------------------------------------*/
/*----------------------------  LOGIN ------------------------------------*/

.instructions, .instructions div{
	background: var(--color2) !important;
}

.instructions .btn{
	border-radius: 50% ;
}

.input-group span{
	background: var(--color2) !important;
}

.input-group-password span{
    border-top: solid 1px var(--color0) !important;
}


.form, .modal-content, .modal-content .btn, .form .btn{
    border: solid 0.04px var(--color0) !important;
}

.alert, .alert-danger{
    color: var(--color0);
	background: var(--negativo) !important;
}


/*------------------------ QUIZ --------------------------*/
.qnbutton {
	color: var(--color1) !important;
}

.info, .formulation {
	background: var(--color1) !important;
}

.formulation h4, .formulation div{
	color: var(--color0) !important;
}

.card-body .main .answersaved{
	background: var(--color2) !important;
}

.answersaved {
    color: var(--color0) !important;
}

.qn_buttons .answersaved{
    background: var(--positivo) !important;
}

.qnbutton {
    background: var(--color1) !important;
}
}
