css:
@-moz-document url-prefix("https://www.khronos.org/opengl/wiki/") {
body, #mw-head, #catlinks { background-color: #202020; }
.mw-body, .mw-redirectedfrom {
    color: #a8bfce;
    background-color: #1f3450 !important;
}
div.vectorTabs span {
    background-image: none;
    background-color: #202020;
}
h1, h2, h3, h4 {
    color: #b9cde1;
}
div#toc,
.side-list-contents,
table.infobox
{
    background-color: #243c5d !important;
}
table.wikitable {
    color: #a7bace;
    background-color: #1c2f4a !important;
}
table.wikitable th {
    color: #1a2739;
    background-color: #71a5d8 !important;
}
table {
    background-color: #1f3450 !important;
}
.tocnumber {
    color: #6c9cbc;
}
a {
    color: #bf93e6 !important;
}
a:visited {
    color: #9283cc !important;
}
pre, code {
    color: #96dd8e;
    background-color: #162937;
}
.tpl-code {
    color: #96dd8e;
    background-color: inherit;
}
.tpl-enum {
    color: #8ace82;
    background-color: #252525;
}
.funcdef {
    background-color: #162937;
}
/*eof*/
}
