css:
@-moz-document domain(moddb.com) {
div.normalbox div.body{background-color:#000!important;border-top:0px solid #000!important;}
div.row:nth-of-type(2n-1),div.baroption:nth-of-type(2n-1){background-color:#111!important;border-top:0px solid #000!important;}
textarea, input.text{background-color:#111!important;}
div.normalbox div.table div.row:nth-of-type(2n-1) div.content p{border-top:0px solid #000!important;}
div.normalbox div.table div.content p{border-top:0px solid #000!important;}
div.normalbox div.table h4 a, div.normalbox div.table span.heading a{color:#ccc!important;}
div.normalbox div.tablerelated a.heading{color:#ccc!important;}
div.normalbox div.body h1, div.normalbox div.body h2, div.normalbox div.body h3, div.normalbox div.body h4, div.normalbox div.body h5, div.normalbox div.body h6, div.normalbox div.body p, div.normalbox div.body blockquote, div.normalbox div.body pre, div.normalbox div.body dl, div.normalbox div.body table{color:#ccc!important;}
span.spoiler{color:#ccc!important;filter:invert(50%)!important;}
}
