input[type="text"],textarea,[class^="___community-select-box"]{background-color:#f0f0f0}
    
/* tags */
    ul[class^="___suggest___"]{background-color:#252525}
    ul[class^="___suggest___"] > *:hover{background-color:#2e2e2e}
    [class^="___tag-widget___"]{background-color:#393939}
    ul[class^="___tag-list"] > li[class^="___tag___"] > span[class^="___tag___"]{background-color:#595959;border:1px solid #8e8e8e;}
    button[class^="___register-button___"]{color:#fff !important;border:1px solid #9d5100;background-color:#d27600;}
    
    
/* konomi tag */
[class^="___konomi-tag-setting-button___"]{background-color:#e36a21;border:4px solid #ffbe258c;}
[class^="___konomi-tag-setting-button___"]:hover{background-color:#a24914;border:4px solid #9972158c;}
[class^="___konomi-tag-setting-panel___"]{background-color:#353535}
[class^="___konomi-tag-list-section___"] > [class*="___tag-list___"]{background-color:#45454d}
[class^="___konomi-tag-list-section___"] > [class*="___tag-list___"] > li > span[class^="___tag___"]{border:1px solid #6c6c6c;background-color:#464646}
[class^="___close-button___"]{color:#fff !important}
}

@-moz-document url-prefix("https://launch.spi.nicovideo.jp/") {
.ItemDialogContent_Title,.ItemDialogContent_Description{color:#ddd}
li > button[value="ネタを削除"],[value="リクエストしたユーザーをNG登録"]{background-color:#444}
button[value="ネタを削除"]:hover,button[value="リクエストしたユーザーをNG登録"]:hover{background-color:#444}
div[class^="___menu-popup-control"] > ul[class^="___list___"]::before{background-color:#444}
}

