css:
@-moz-document domain("cetmar17.com") {
#nav-drawer{
    background-color: #1c2037;
    height: calc(100% - 70px);
    border-top: 1px solid #374850;
}

#nav-drawer .metismenu .list-group-item a span{
    padding-left: 12px;
    vertical-align: top;
}

#nav-drawer .metismenu .list-group-item:hover{
    background-color: #3d3d4a;
}

#nav-drawer .metismenu .list-group-item.active a{
    font-weight: bold;
    color: #5cc9f5;
}

#nav-drawer-footer {
    display: none !important;
}

.fixed-top.navbar.navbar-light.navbar-expand.moodle-has-zindex {
    background-color: #1c2037;
}

.fixed-top.navbar.navbar-light.navbar-expand.moodle-has-zindex ::before{
    color: #fff;
}

.role-estudiante {
    background-color: #eecc61;
    padding-right: 12px;
    padding-left: 12px;
    color: #000;
    font-height: bold;
}

#action-menu-1-menubar :after {
    color: #fff;
}

.d-inline-block.mr-3.drawer-toggle {
    background-color: #1c2037;
}

.fixed-top.navbar.navbar-light.navbar-expand.moodle-has-zindex ::before {
    color: #fff;
}

.action-menu-trigger a:focus{
	outline:none;
}

.page-header-headings h1 {
    color: #000;
}
}
