css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("bing.com"), 
url("http://www.bing.com") 
{
 

.content_wrap 
{
 opacity: .65 ! important;
margin:15% ! important;
margin-top:5% ! important;

}
#container{height:500px ! important;
 }
#sw_content{max-height:250px ! important;}
 body{background: url("http://i577.photobucket.com/albums/ss216/rover35si/index-2.gif")!important;
}
#sb_foot{display:none !important;
}
#container *{-moz-border-radius:15px ! important;}
.sc_grad{background:transparent ! important;}
#sw_hdr{border:none ! important;}
#content>div#sw_content>div#bgDiv{background: black !important;
}
.content_wrap{border:none ! important;}
a{color:#FF8000 !important;
}
h3{color:white !important;
}
cite{color:yellow !important;
}
span{color:brown !important;}
}

@-moz-document url-prefix("http://www.bing.com/search?"),
url-prefix("http://www.bing.com/images/search?")  
{
.sw_bd{margin-left:300px !important;padding-right:20px !important;margin-top:0px !important;

}

.sw_tb{background: black !important;position:fixed !important;}

#results_area{width:100% !important; }
#results > ul #wg0 .sb_results > li,p{width:900px !important; }
p{color:white !important;
}


#results,sb_ph{margin-top:50px !important;
}

#sw_nav a{color:blue !important;
}
.sa_cpt>div.sa_cpti{margin-left:300px !important;
}

#sidebar, .sb_adsW {display: none !important;}


body.WideView, .Content {min-width: 400px !important; width: 100% !important;}
body {min-width: 400px !important;}


.scopeTabs {top: 24px !important;}
.scopeTabsSelectedNB, .SubScopeSwitch {color: #FFFFFF !important;}


.ansC {background-color:transparent; margin-left:0px !important; margin-bottom:-20px;}
.ans3 {max-width: 90% !important;}

/* numbered results */
#results_container #results ul li {list-style-type:decimal !important;color:green !important;}


}
@-moz-document url-prefix("http://www.bing.com/settings") 
{

body *{background: url("http://i577.photobucket.com/albums/ss216/rover35si/index-2.gif")!important;
}
div#content{background:transparent ! important;
}
p{color:yellow !important;
}
label{color:white !important;
}
input{color:green !important;
}


}
