css:
@-moz-document domain("subsplease.org") {
/* VARIABLES */

:root {
	--primary-bg-color: #111311;
	--secondary-bg-color: #222522;
	--tertiary-bg-color: #333633;
	--quaternary-bg-color: #444B44;
	--primary-text-color: #EEFFEE;
	--secondary-text-color: #DDEFDD;
	--tertiary-text-color: #BBCCBB;
	--accent-color: #11BB66;
	--border-radius: 5px;
}

/* SELECTORS */

::placeholder {
	color: var(--tertiary-text-color);
	opacity: 0.7;
}
::selection {
	background-color: var(--accent-color);
	color: var(--primary-bg-color);
}
}

@-moz-document regexp("(.+)subsplease.org\\/(?!xdcc)(.+)?") {
/* GENERAL TAGS */

body {
	background: var(--primary-bg-color);
	color: var(--secondary-text-color);
}
* {
	transition: all 0.15s ease-out;
}
h1, h2, h3, h4, h5, h6 {
	color: var(--primary-text-color);
}
h2 {
	margin-bottom: 1rem;
}
a, a:visited {
	color: var(--secondary-text-color);
}
a:hover, a:focus, a:active {
	color: var(--accent-color) !important;
}
table th, table td {
	border-bottom-color: var(--quaternary-bg-color);
}
button:focus {
	background-color: var(--accent-color);
}
button:active {
	bottom: auto;
}
label {
	cursor: text;
}

/* HEADER */

#masthead {
	background: var(--secondary-bg-color);
}
.site-title a {
	color: var(--primary-text-color) !important;
}
.site-title a:hover {
	color: var(--accent-color) !important;
}
.site-title a:focus,
.site-title a:active {
	opacity: 1;
}
.site-description {
	color: var(--secondary-text-color) !important;
}
.sf-menu > li > a,
.sf-menu > li > a:visited {
	color: var(--primary-text-color);
}
.sf-menu > li > a:hover,
.sf-menu > li > a:focus,
.sf-menu > li > a:active {
	color: var(--accent-color);
	opacity: 0.7;
}
.main-navigation {
	border-color: var(--quaternary-bg-color);
}
.toggle-menu-control::before,
.toggle-menu-control span {
	color: var(--primary-text-color);
}
.site-header-menu-responsive {
	background-color: var(--primary-bg-color);
}
.site-header-menu-responsive .header-menu-responsive-close {
	color: var(--secondary-text-color);
}
.site-header-menu-responsive .header-menu-responsive-close:hover {
	color: var(--accent-color);
}
.header-menu-responsive a,
.header-menu-responsive a:visited {
	color: var(--secondary-text-color);
}
.header-menu-responsive a:hover,
.header-menu-responsive a:focus,
.header-menu-responsive a:active {
	color: var(--accent-color);
	opacity: 0.7;
}

/* BODY */
.alert {
	margin-bottom: 30px;
	background: var(--secondary-bg-color);
}
.site-content .site-content-inside {
	padding: 30px;
}
.post-wrapper-single .post-wrapper-hentry {
	background: var(--secondary-bg-color);
	border-radius: var(--border-radius);
}
.post-content-wrapper-single .entry-header-wrapper {
	border-bottom-color: var(--quaternary-bg-color);
}

/* BODY: Anime entry listings */
.releases-header {
	padding: 0px 10px 0px 0px;
	margin-bottom: 1rem;
}
.releases-header h2 {
	padding: 0px;
}
.releases-header h2 img.emoji {
	float: left;
	filter: saturate(0) contrast(0) brightness(1.8);
	margin-right: 10px !important;
}
.releases-header form {
	margin-left: 0px;
}
.search-box {
	border-radius: var(--border-radius);
	overflow: hidden;
}
input.search-box-input,
input.search-box-input:focus {
	background-color: var(--quaternary-bg-color) !important;
	color: var(--secondary-text-color);
	border-radius: var(--border-radius) 0px 0px var(--border-radius) !important;
	border: 1px solid var(--quaternary-bg-color);
	border-right: none;
	font-size: 14px;
	padding: 0.25rem 0.625rem;
}
input.search-box-input:focus {
	border-color: var(--accent-color);
	background-color: var(--tertiary-bg-color) !important;
}
.search-button {
	background-color: var(--accent-color);
	border-color: var(--accent-color);
	border-radius: 0px var(--border-radius) var(--border-radius) 0px;
	font-size: 14px;
	color: var(--primary-text-color);
}
.search-button:hover {
	background-color: var(--accent-color);
	filter: brightness(1.2);
}
.badge {
	background: var(--tertiary-bg-color);
}
.badge-front {
	color: var(--tertiary-text-color);
}
.badge-front:hover {
	color: var(--accent-color);
}
.download-links a:hover {
	color: var(--accent-color) !important;
}
.time-new-badge {
	background-color: var(--accent-color);
	border-radius: var(--border-radius);
	color: var(--primary-text-color);
}
.time-new-badge::selection {
	background-color: var(--primary-text-color);
	color: var(--accent-color);
}
#preview-image-plugin-overlay {
	background-color: var(--secondary-bg-color) !important;
	border-color: var(--tertiary-bg-color) !important;
	transition: none;
	pointer-events: none;
}
#latest-load-more span {
	border-radius: var(--border-radius);
	background-color: var(--accent-color);
	color: var(--primary-text-color);
}
#latest-load-more span:hover {
	background-color: var(--accent-color);
	filter: brightness(1.2);
}
.entry-meta .entry-meta-icon a::before,
.entry-meta .entry-meta-icon::before {
	color: var(--accent-color);
}
.episode-title:hover {
	color: var(--accent-color);
	cursor: pointer;
}
#show-release-table .release-item-time {
	vertical-align: top;
}
.download-links .badge {
	margin-right: 0px;
}

/* BODY: Blog entries */

.post-navigation {
	background: none;
	border: none;
	padding: 1rem 1.875rem;
}
.post-navigation .meta-nav {
	color: var(--accent-color);
}
#disqus_thread {
	margin-top: 0px;
}

/* BODY: Sidebar */ 

.sidebar {
	background: var(--secondary-bg-color);
	border-radius: var(--border-radius);
	margin-bottom: 30px;
}
.sidebar:first-child > h2::before {
	font-family: "Font Awesome 5 Free";
	font-weight: 400;
	margin-right: 10px;
	content: "\f017";
}
#current-time + h2::before {
	font-family: "Font Awesome 5 Free";
	font-weight: 400;
	margin-right: 10px;
	content: "\f073";
}
#schedule-table + h2::before {
	content: "\f1ea";
	font-weight: 100;
}
.wc_time {
	margin-left: 5px;
}
img.emoji[alt="✔"] {
	filter: invert(100%);
	filter: hue-rotate(250deg) brightness(2.5);
	margin-right: 6px !important;
}
html[data-theme="dark"] img.emoji[alt="✔"] {
	filter: hue-rotate(250deg) brightness(2.5);
}
}

@-moz-document regexp("(.+)subsplease.org\\/xdcc(.+)") {
/* TAGS */

body {
	background: var(--primary-bg-color);
	color: var(--primary-text-color);
}
a {
	color: var(--secondary-text-color);
}
a:hover {
	color: var(--tertiary-text-color);
}
input, th {
	border-color: var(--tertiary-bg-color);
}

/* BODY: Sidebar */

.botlist {
	background-color: var(--secondary-bg-color);
	border-color: var(--tertiary-bg-color);
}
.botlist h3 {
	border-color: var(--tertiary-bg-color);
	color: var(--primary-text-color);
	margin-bottom: 5px;
}

/* BODY: Packlist */

div.header,
#searchdiv {
	background-color: var(--primary-bg-color);
	border-color: var(--tertiary-bg-color);
}
div.header {
	height: auto;
}
div.header h1 {
	color: var(--primary-text-color);
	margin-top: 24px;
	padding: 0px;
}
#listname {
	margin: 5px 0px;
}
.search, .default {
	color: var(--tertiary-text-color);
}
input[type="submit"].search {
	width: auto !important;
}
.anime0 {
	background-color: transparent;
}
.anime1 {
	background-color: var(--secondary-bg-color);
}
.anime0:hover, .anime1:hover {
	background-color: var(--accent-color);
	color: var(--primary-bg-color);
}
div.footer {
	background-color: transparent;
	border-color: var(--tertiary-bg-color);
}
div.footer .default {
	color: var(--secondary-text-color);
}
div.footer .default:hover {
	color: inherit;
}
#status {
	background-color: #303030;
}
.loading {
	background-image: url(https://subsplease.org/xdcc/load1.gif)
}
}

@-moz-document regexp("(.+)disqus.com\\/embed\\/comments\\/(.+)f\\=subsplease(.+)") {
:root {
	--primary-bg-color: #111311;
	--secondary-bg-color: #222522;
	--tertiary-bg-color: #333633;
	--quaternary-bg-color: #444B44;
	--primary-text-color: #EEFFEE;
	--secondary-text-color: #DDEFDD;
	--tertiary-text-color: #BBCCBB;
	--accent-color: #11BB66;
	--border-radius: 5px;
}
* {
	outline: 0 !important;
}
body.dark,
.dark .nav-tab > a {
	color: var(--primary-text-color) !important;
}
.dark .comment-policy,
.dark .highlighted > .post-content {
	background: var(--secondary-bg-color);
}
.post-content .post-body spoiler {
	background: var(--tertiary-bg-color);
}
.comment-policy,
.highlighted > .post-content {
	border-radius: var(--border-radius);
}
.post:last-of-type .post-content {
	margin-bottom: 0px;
}
.post:last-of-type .children {
	margin-top: 24px;
}
.load-more {
	margin: 0;
}
.load-more__button {
	background-color: var(--accent-color) !important;
	color: var(--primary-bg-color) !important;
	transition: opacity 0.2s;
}
.load-more__button:hover {
	filter: brightness(1.1);
	transition: opacity 0.2s;
}
.load-more__button:focus {
	outline: none;
	opacity: 0;
	transition: opacity 0.2s;
}
.textarea-outer-wrapper .textarea-wrapper,
.textarea,
.dark .input--text,
.dropdown-menu {
	background: var(--secondary-bg-color);
	border-color: var(--quaternary-bg-color);
	color: var(--primary-text-color);
	border-width: 1px;
	transition: filter 0.2s;
}
.dark .input--text:focus {
	filter: brightness(1.2);
}
.dropdown-menu a {
	color: var(--secondary-text-color);
}
.dropdown-menu .active > a,
.dropdown-menu .active > a:focus,
.dropdown-menu .active > a:hover,
.dropdown-menu li > a:focus,
.dropdown-menu li > a:hover {
	background-color: var(--accent-color);
	color: #FFF;
}
.dark .textarea,
.post-actions {
	border-color: var(--quaternary-bg-color);
	border-width: 1px;
}
.textarea-outer-wrapper .textarea-wrapper:focus-within,
.textarea-outer-wrapper .textarea-wrapper:focus-within .post-actions {
	filter: brightness(1.2);
	transition: filter 0.2s;
}
.placeholder {
	color: var(--secondary-text-color);
	opacity: 0.5;
}
.wysiwyg__item {
	filter: hue-rotate(-90deg) brightness(1.7);
}
.tooltip {
	background: var(--secondary-bg-color);
}
.voters-outer::before,
.voters-outer::after {
	display: none;
}
.tooltip-point::after, .tooltip-point::before {
	background-color: var(--secondary-bg-color);
}
.voters-outer .voters .user .username,
.voters-outer .voters .user .username:hover {
	color: var(--primary-text-color);
}
.post-content .post-menu .dropdown-toggle {
	border-color: var(--quaternary-bg-color);
}
.dark .proceed__button,
.dark .proceed__button:hover {
	background: var(--tertiary-bg-color);
	color: var(--secondary-text-color);
	box-shadow: none;
}
.dark .proceed__button:hover {
	filter: brightness(1.2);
}
.post-menu-item a span {
	line-height: 1.4;
}
.dropdown-toggle,
.dark .post-content .post-menu .collapse a, .dark .post-content .post-menu .expand a {
	color: var(--tertiary-text-color) !important;
	opacity: 1;
}
.dropdown-toggle:hover,
.post-content .post-menu .collapse a:hover, .post-content .post-menu .expand a:hover {
	color: var(--accent-color) !important;
}
#footer {
	display: none; 
}
}

@-moz-document url-prefix("https://discord.com/widget?id=764264480103989258") {
div[class^="widgetHeader"] {
	background: #444B44;
}
a[class^="widgetLogo"],
span[class^="widgetHeaderCount"] {
	opacity: 0.8;
}
div[class^="widgetBody"] {
	background-color: #111311 !important;
	background-image: url(https://discord.com/assets/db9fd9dac08621e31b06609781c8851c.png),url(https://discord.com/assets/1c7d080332a1c9778182bc19c2f407bc.png) !important;
}
div[class^="widgetFooter"] {
	background-color: #333633 !important;
}
span[class^="widgetFooterInfo"] {
	color: #EEFFEE !important;
	opacity: 0.6 !important;
}
div[class^="widgetMembersOnline"],
div[class^="widgetChannelName"] {
	color: #BBCCBB !important;
	opacity: 0.8;
}
span[class^="widgetMemberName"] {
	color: #BBCCBB !important;
	opacity: 0.7;
}
span[class^="widgetMemberGame"] {
	color: #BBCCBB !important;
	opacity: 0.4;
}
}
