@-moz-document domain("subsplease.org") {
/* VARIABLES */

:root {
	--primary-bg-color: #111311;
	--secondary-bg-color: #222522;
	--tertiary-bg-color: #333633;
	--quaternary-bg-color: #444B44;
	--primary-text-color: #EEFFEE;
	--secondary-text-color: #DDEFDD;
	--tertiary-text-color: #BBCCBB;
	--accent-color: #11BB66;
	--border-radius: 5px;
}

/* SELECTORS */

::placeholder {
	color: var(--tertiary-text-color);
	opacity: 0.7;
}
::selection {
	background-color: var(--accent-color);
	color: var(--primary-bg-color);
}
}

@-moz-document regexp("(.+)subsplease.org\\/(?!xdcc)(.+)?") {
/* GENERAL TAGS */

body {
	background: var(--primary-bg-color);
	color: var(--secondary-text-color);
}
* {
	transition: all 0.15s ease-out;
}
h1, h2, h3, h4, h5, h6 {
	color: var(--primary-text-color);
}
h2 {
	margin-bottom: 1rem;
}
a, a:visited {
	color: var(--secondary-text-color);
}
a:hover, a:focus, a:active {
	color: var(--accent-color);
}
table th, table td {
	border-bottom-color: var(--quaternary-bg-color);
}
button:focus {
	background-color: var(--accent-color);
}
button:active {
	bottom: auto;
}

/* HEADER */

#masthead {
	background: var(--secondary-bg-color);
}
.site-title a {
	color: var(--primary-text-color) !important;
}
.site-title a:hover {
	color: var(--accent-color) !important;
}
.site-title a:focus,
.site-title a:active {
	opacity: 1;
}
.site-description {
	color: var(--secondary-text-color) !important;
}
.sf-menu > li > a,
.sf-menu > li > a:visited {
	color: var(--primary-text-color);
}
.sf-menu > li > a:hover,
.sf-menu > li > a:focus,
.sf-menu > li > a:active {
	color: var(--accent-color);
	opacity: 0.7;
}
.main-navigation {
	border-color: var(--quaternary-bg-color);
}
.toggle-menu-control::before,
.toggle-menu-control span {
	color: var(--primary-text-color);
}
.site-header-menu-responsive {
	background-color: var(--primary-bg-color);
}
.site-header-menu-responsive .header-menu-responsive-close {
	color: var(--secondary-text-color);
}
.site-header-menu-responsive .header-menu-responsive-close:hover {
	color: var(--accent-color);
}
.header-menu-responsive a,
.header-menu-responsive a:visited {
	color: var(--secondary-text-color);
}
.header-menu-responsive a:hover,
.header-menu-responsive a:focus,
.header-menu-responsive a:active {
	color: var(--accent-color);
	opacity: 0.7;
}

/* BODY */

.site-content .site-content-inside {
	padding: 30px;
}
.post-wrapper-single .post-wrapper-hentry {
	background: var(--secondary-bg-color);
	border-radius: var(--border-radius);
}
.post-content-wrapper-single .entry-header-wrapper {
	border-bottom-color: var(--quaternary-bg-color);
}

/* BODY: Anime entry listings */
.releases-header {
	padding: 0px 10px 0px 0px;
	margin-bottom: 1rem;
}
.releases-header h2 {
	padding: 0px;
}
.releases-header h2 img.emoji {
	float: left;
	filter: saturate(0) contrast(0) brightness(1.8);
	margin-right: 10px !important;
}
.releases-header form {
	margin-left: 0px;
}
.search-box {
	border-radius: var(--border-radius);
	overflow: hidden;
}
input.search-box-input,
input.search-box-input:focus {
	background-color: var(--quaternary-bg-color) !important;
	color: var(--secondary-text-color);
	border-radius: var(--border-radius) 0px 0px var(--border-radius);
	border: none;
	font-size: 14px;
	padding: 0.25rem 0.625rem;
}
input.search-box-input:focus {
	filter: brightness(1.2);
}
.search-button {
	background-color: var(--accent-color);
	border-color: var(--accent-color);
	border-radius: 0px var(--border-radius) var(--border-radius) 0px;
	font-size: 14px;
	color: var(--primary-text-color);
}
.search-button:hover {
	background-color: var(--accent-color);
	filter: brightness(1.2);
}
.badge {
	background: var(--tertiary-bg-color);
}
.badge-front {
	color: var(--tertiary-text-color);
}
.badge-front:hover {
	color: var(--accent-color);
}
.time-new-badge {
	background-color: var(--accent-color);
	border-radius: var(--border-radius);
	color: var(--primary-text-color);
}
.time-new-badge::selection {
	background-color: var(--primary-text-color);
	color: var(--accent-color);
}
#preview-image-plugin-overlay {
	background-color: var(--secondary-bg-color) !important;
	border-color: var(--tertiary-bg-color) !important;
	transition: none;
	pointer-events: none;
}
#latest-load-more span {
	border-radius: var(--border-radius);
	background-color: var(--accent-color);
	color: var(--primary-text-color);
}
#latest-load-more span:hover {
	background-color: var(--accent-color);
	filter: brightness(1.2);
}

/* BODY: Sidebar */ 

.sidebar {
	background: var(--secondary-bg-color);
	border-radius: var(--border-radius);
	margin-bottom: 30px;
}
.sidebar:first-child > h2::before {
	font-family: "Font Awesome 5 Free";
	font-weight: 400;
	margin-right: 10px;
	content: "\f017";
}
#current-time + h2::before {
	font-family: "Font Awesome 5 Free";
	font-weight: 400;
	margin-right: 10px;
	content: "\f073";
}
.wc_time {
	margin-left: 5px;
}
img.emoji[alt="✔"] {
	filter: invert(100%);
	filter: hue-rotate(250deg) brightness(2.5);
	margin-right: 6px !important;
}
html[data-theme="dark"] img.emoji[alt="✔"] {
	filter: hue-rotate(250deg) brightness(2.5);
}
}

@-moz-document regexp("(.+)subsplease.org\\/xdcc(.+)") {
/* TAGS */

body {
	background: var(--primary-bg-color);
	color: var(--primary-text-color);
}
a {
	color: var(--secondary-text-color);
}
a:hover {
	color: var(--tertiary-text-color);
}
input, th {
	border-color: var(--tertiary-bg-color);
}

/* BODY: Sidebar */

.botlist {
	background-color: var(--secondary-bg-color);
	border-color: var(--tertiary-bg-color);
}
.botlist h3 {
	border-color: var(--tertiary-bg-color);
	color: var(--primary-text-color);
	margin-bottom: 5px;
}

/* BODY: Packlist */

div.header,
#searchdiv {
	background-color: var(--primary-bg-color);
	border-color: var(--tertiary-bg-color);
}
div.header {
	height: auto;
}
div.header h1 {
	color: var(--primary-text-color);
	margin-top: 24px;
	padding: 0px;
}
#listname {
	margin: 5px 0px;
}
.search, .default {
	color: var(--tertiary-text-color);
}
input[type="submit"].search {
	width: auto !important;
}
.anime0 {
	background-color: transparent;
}
.anime1 {
	background-color: var(--secondary-bg-color);
}
.anime0:hover, .anime1:hover {
	background-color: var(--accent-color);
	color: var(--primary-bg-color);
}
div.footer {
	background-color: transparent;
	border-color: var(--tertiary-bg-color);
}
div.footer .default {
	color: var(--secondary-text-color);
}
div.footer .default:hover {
	color: inherit;
}
#status {
	background-color: #303030;
}
.loading {
	background-image: url(https://subsplease.org/xdcc/load1.gif)
}
}
