css:
@-moz-document domain(music.amazon.com) {
  .border-gradient2 .center {
	background-color: black;
  }

  .dragonfly .searchBox {
	color: white;
  }

  .suggestions-list {
	background-color: #0d0d0d;
  }
  .suggestions-list li a:hover,
  .suggestions-list a.selectedItem:hover {
	color: black !important;
  }

  a,
  a:link,
  a:visited {
	color: #95c82e !important;
  }

  navigationToggle .title {
	color: #919191;
  }

  #left-content.dragonfly.normalColorScheme {
	background: #222;
  }

  #searchMusic {
	background: #222 !important;
  }

  .checkboxContainer .checkboxLabel {
	background-color: black;
  }

  #dragonflyTransport.normalColorScheme {
	background: black;
	border-bottom: 1px solid white;
  }

  #dragonflyLeftNav .newPlaylist {
	background-color: #3f4040;
  }

  #dragonflyLeftNav .newPlaylist:hover {
	background-color: black;
 }

  .horizontalTile .details .primaryTitle,
  .horizontalTile .details .primaryTitle a,
  .horizontalTile .detailsWithIndex .primaryTitle,
  .horizontalTile .detailsWithIndex .primaryTitle a {
	color: #848d9f;
	font-size: .9375rem;
  }
  /* Hamburger menu for playlist */
  .dragonfly section.menuPanel {
	background-color: black;
  }
  .playqueue-view .current-track {
	background-color: black;
	color: white;
  }

  #contextMenu li span {
	color: white;
  }
  #contextMenu li:hover span {
	color: black;
  }

  /* Table at '/my/songs' path */
  .albumList .subHeader,
  .songList .subHeader {
	background-color: inherit;
	line-height: 20px;
	text-transform: uppercase;
  }

  .playlistDetailsListItem .title {
	color: #9d9d9d;
  }

  .playlistDetailsListItem:hover:not(.emptyRow):not(.header) td {
	background-color: #313131;
  }

  .albumDetailsHeader .artistLink a {
	color: #8a8a8a;
  }

  .viewHeader .viewTitle {
	color: #557d03;
  }

  .albumDetailsHeader .artistLink a,
  playlistDetailsListItem .artist a {
	color: #95c82e !important;
  }
}
