/* i really want this to be global */
/* Vivaldi - Prevent speed dial folder transitions */
.sdwrapper .left-enter,
.sdwrapper .left-leave,
.sdwrapper .right-enter,
.sdwrapper .right-leave,
.sdwrapper .up-enter,
.sdwrapper .up-leave,
.sdwrapper .down-enter,
.sdwrapper .down-leave
{
  transition: none !important; 
}
/* Hide speed dial shadows */
.dials .dial > .thumbnail-image
{
  box-shadow: none !important;
}
