@-moz-document domain("forums.thesims.com") {
/* Darkmode for the Sims forums by Seyjin */

/* Couleur principale du texte */
body, .DataTableWrap thead td, .DataTableWrap .CommentDate, .DataTableWrap .CommentDate:hover, .Section-Discussion .CommentMeta a, .Section-Discussion .DiscussionMeta a, .Section-Discussion .Item.rank-blue .Meta, .Section-Discussion .Item.rank-blue .Meta a, .Section-Discussion .Item.rank-blue .Meta a:focus, .Section-Discussion .Item.rank-blue .Meta a:hover, .Section-Discussion .CommentMeta, .Section-Discussion .DiscussionMeta, .Section-Discussion .PageTitle h1, input.DateBox, input.InputBox, input.SmallInput, textarea, .editor-fullpage .TextBoxWrapper .BodyBox, .editor-fullpage .TextBoxWrapper .wysihtml5-sandbox, .nav-me .Username, .info-bar .breadcrumbs, .info-bar .breadcrumbs a, .info-bar .SignInLinks, .info-bar .SignInLinks a, a.Close, .Preview, .hljs, .hljs-keyword, .hljs-selector-tag, .hljs-subst, .Section-Discussion .MessageList .AuthorInfo span,.Section-Profile .User > .H, #Panel .About.P dt, #Panel .About.P, #Panel li.Active a, .Info, ul.token-input-list, .AdvancedSearch select, select, input, .Count {
    color: #ccc;
}

/* Couleur des liens */
#Panel li a, .Section-Profile .TextColor, .DataList a.Delete {
    color: #25a8e0;
}

#Panel li a:hover, .Section-Profile .TextColor:hover {
    color: #68c3ea;
}
/* Aspect de la barre dâ€™info */
.info-bar, .footer {
    background: #1c1b22;
}

.Alert {
    background-color: #e10000;
    border: 1px solid #ff3a3a;
    color: #f5f5f5;
}

.search-bar .Button, .search-bar .Button:hover, .search-bar .Button:focus {
    background-color: #406f08 !important;
}

/* */
.InputAndButton .Handle .Arrow {
    border-color: #fff transparent transparent transparent;
}
/* Recolorisation des icones */
.OptionsTitle, .Sprite16, .ReactSprite, button .Sprite, .QuickSearch .Sprite {
    filter: invert(80%);
}

.HomeCrumb a {
    filter: invert(20%);
}

/* Aspect de la barre de navigation */
.nav-bar {
    background: linear-gradient(rgba(0,0,0,0) 25%,rgba(0,0,0,.1));
    background-color: #222029;
}

.nav-bar li a {
    text-shadow: 0 2px 0 #3b4460;
}

/* Aspect des boutons */
.Button, .Button:focus, body:not(.dataDriven) .Button.Active, body:not(.dataDriven) .ButtonGroup.Open .Button.Handle, body:not(.dataDriven) .Button:focus, body:not(.dataDriven) .Button:active {
    background: linear-gradient(#2096c8,#275d8c);
    border: 1px solid #1e486c;
    color: #f5f5f5;
}

/* Aspect des messages dâ€™information */
.AlertMessage {
    background: #25291b;
    border-color: #020202;
}

.CasualMessage {
    background: #1b2920;
    border-color: #020202;
}

.WarningMessage {
    background: #291b1e;
    border-color: #020202;
}

.InfoMessage {
    background: #1b2920;
    border-color: #020202;
}

.DismissMessage{
    color: #fff;
}

/* Couleur principale du fond de la page */
#Body, div.Popup .Body, .Preview, .hljs, #Panel .About.P, select, input, .token-input-dropdown {
    background-color: #222029;
}

/* Couleur alternative du fond de la page */
.FormWrapper, .editor-fullpage #editor-fullpage-candidate, .editor-fullpage #editor-fullpage-candidate .editor, div.Spoiler.SpoilerConfigured, .Section-Discussion .Message::before, .Info, li.token-input-selected-token, .Count, .DataList a.Delete:hover, .userContent .codeBlock {
    background-color: #1c1b22;
}

/* Couleur des trais du tableau */
.DataTable td, .ChildCategories, .Section-Discussion .Item-BodyWrap, .Section-Discussion .DataList .Item, .Section-Discussion .ItemDiscussion .Discussion, .Section-Discussion .DataList.Comments, .MenuItems hr, .footer-logos, .Section-Discussion .UserQuote, .hljs, #ContentPanel .panel-content, .Section-Profile #ContentPanel, select, li.token-input-token, .Tile, .DismissMessage ~ .DataTableWrap, .DismissMessage ~ .Discussion .PageTitle {
    border-color: #18171e;
}

/* Marques lu/non-lu des messages */
.Item.Read, .Section-Discussion .Item-Header {
    background: linear-gradient(to right,  #1979a2 4px, #fff0 3px,#0001);
}

.Item.New, .Item.Unread, .MessageList #latest ~ .Item-Header {
    background: linear-gradient(to right,  #ff0 4px, #fff0 3px,#0001);
}

.HasNew {
    background: none;
    color: #ff0;
    font-size: 0px;
}

.HasNew:before {
    content: "(â€¯";
    font-size: 11px;
    position: relative;
    bottom: 1px;
}

.HasNew:after {
    content: "â€¯)";
    font-size: 11px;
    position: relative;
    bottom: 1px;
}

/* Couleur des Ã©tiquettes */
.DiscussionName .Tag {
    background: none;
    font-size: 0px;
    padding: 0px;
}

.DiscussionName .Tag-Announcement:before {
    content: "ðŸ“Œ";
    font-size: 12px;
}

.DiscussionName .Tag-Closed:before {
    content: "ðŸ”’";
    font-size: 11px;
}

.DiscussionName .CategoryDescription, .DiscussionName .Item .MItem, .DiscussionName .Number, .DiscussionName .nTag {

    font-size: 11px;
    line-height: inherit;

}


/* En-tÃªtes spÃ©ciales des messages */
.Section-Discussion .Rank-Admin .Message::before {
    background-color: #406f08;
}

.Section-Discussion .Item.rank-blue .Message::before {
    background-color: #0b5777;
}

/* Indicateur du numÃ©ro de la page */
.Pager .Highlight {
    background-color: #1c1b22;
    border-radius: 10px;
    color: #ccc;
}

/* Couleur du texte indiquant quâ€™un message a Ã©tÃ© modifiÃ© */
div.PostEdited {
    color: #ff3a3a;
}

/* Spoiler spoiler spoiler */
div.Spoiler div.SpoilerTitle input, .NavButton, body:not(.dataDriven) .ButtonGroup.Open .NavButton.Handle {
    background-color: #222029;
    border-color: #18171e;
    color: #25a8e0;
}

/*  Signaler un abus / Citer / IntÃ©ressant / GÃ©nial ! / LOL  */
.Reactions .ReactButton:hover {
    opacity: 1;
}

/* Aspect des boites dâ€™Ã©dition de textes */
.InputBox, input:focus, textarea:focus, textarea, .Activities a.CommentLink, .Activities a.CommentLink:hover, ul.token-input-list, .AdvancedSearch select {
    background: #222029 !important;
    border-color: #18171e;
}

:focus {
    outline-style: none;
}

.Section-Discussion  .Preview .Message::before {
    display: none;
}
/* Upload une image */
.editor-upload-progress {
    filter: brightness(0.3) contrast(120%);
}

/* Aspect des menus */
.MenuItems, .FlyoutMenu, .atwho-view {
    border-color: #000;
    background: #222029;
    color: #ccc;
}

.Flyout::before, .Flyout::after {
    border-bottom: 7px solid #000;
    top: -7px;
}

.MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active {
    color: #ccc !important;
}

.Dropdown.MenuItems a:hover, .Flyout.MenuItems a:hover, .editor-dd-emoji + .editor-insert-dialog .emoji-wrap:hover {
    background-color: #18171e88 !important;
    color: #ccc !important;
}

/* Aspet de la liste des destinataires pour un nouveau message privÃ© */
.token-input-dropdown-item, .token-input-dropdown-item2, .token-input-dropdown {
    background-color: #222029 !important;
    border-color: #18171e !important;
}

.token-input-selected-dropdown-item {
    background-color: #1c1b22 !important;
}
}
