css:
@-moz-document url-prefix("https://genshin-impact-map.appsample.com") {
#topnav {
    display: none !important;
    background: linear-gradient(90deg,transparent,#000);
}

body {
    color: #f7cec5 !important;
    cursor: url('https://i.imgur.com/UnPYb4E.png'), auto !important;
}
.btn:not(:disabled):not(.disabled) {
    cursor: url('https://i.imgur.com/PfRiIQW.png'), auto !important;
}

a:-webkit-any-link {
    cursor: url('https://i.imgur.com/PfRiIQW.png'), auto !important;
}

[type=button]:not(:disabled), [type=reset]:not(:disabled), [type=submit]:not(:disabled), button:not(:disabled) {
    cursor: url('https://i.imgur.com/PfRiIQW.png'), auto !important;
}

div[style*='cursor: url("https://maps.gstatic.com/mapfiles/openhand_8_8.cur"), default;'] {
  cursor: url("https://i.imgur.com/UnPYb4E.png"), auto !important;
}

div[style*='cursor: pointer;'] {
  cursor: url("https://i.imgur.com/jYQHml1.png"), auto !important;
}

div[style*='url("https://maps.gstatic.com/mapfiles/closedhand_8_8.cur"), move'] {
    cursor: url("https://i.imgur.com/jYQHml1.png"), auto !important;
}

/* General Stuff */

u {
    font-weight: 700;
    text-decoration: none;
    color: #e7baea;
}

.gm-style .gm-style-iw-t::after {
    background: #d2632873;
    height: 15px;
    position: absolute;
    transform: translate(-50%,-50%) rotate(-45deg);
    width: 15px;
}

.gm-style .gm-style-iw-c {
    position: absolute;
    box-sizing: border-box;
    cursor: url('https://i.imgur.com/UnPYb4E.png'), auto !important;
    transform: translate(-50%,-100%);
    background-color: #d2632873 !important;
    border-radius: 12px;
    padding: 12px;
}

#sidebar {
    position: fixed;
    top: 0;
    left: 0;
    bottom: 0;
    width: 400px;
    transition: left .3s ease;
    z-index: 1040;
    padding-top: 7.875rem;
    padding-bottom: 0px !important;
}

#sidebar .sidebar-footer {
    background-image: none !important;
    /* background-color: #4c2e0b91 !important;
    height: 2.55%; */
    display: none !important;
    overflow: hidden;
}
#sidebar .siderbar-header nav {
    background: linear-gradient(0deg, rgba(2,0,36,0) 0%, rgba(247,206,197,0.30015756302521013) 0%, rgba(242,195,186,1) 0%, rgba(234,177,167,0) 0%, rgba(166,11,0,0.3) 52%, rgba(252,17,0,0.4009978991596639) 100%, rgba(77,77,77,0.35) 100%, rgba(77,77,77,0.35) 100%, rgba(0,0,0,0.35) 100%, rgba(0,0,0,0.35) 100%, rgba(255,255,255,0.35) 100%, rgba(74,74,74,0.2) 100%);
    background-size: cover;
}

.text-info {
    color: #ffcc8e!important;
}

.custom-select[data-v-1edfb52c] {
    background-color: #f375126b;
    color: #ffe9c0;
    border-radius: 8px;
    border-color: transparent;
}
.input-group>.input-group-append:last-child>.btn:not(:last-child):not(.dropdown-toggle), .input-group>.input-group-append:last-child>.input-group-text:not(:last-child), .input-group>.input-group-append:not(:last-child)>.btn, .input-group>.input-group-append:not(:last-child)>.input-group-text, .input-group>.input-group-prepend>.btn, .input-group>.input-group-prepend>.input-group-text {
    display: none;
}

.text-primary {
    color: #c0ff00cf !important;
    text-decoration: none;
}
.text-primary:hover {
    color: #ffa200cf !important;
    text-decoration: none;
}

textarea {
    cursor: url('https://i.imgur.com/ehs94uQ.png'), auto !important;
}

.bg-white {
    background-color: #4c2e0b91 !important;
}

.nav-tabs {
    border-bottom: 1px solid transparent;
}
.nav-tabs .nav-item.show .nav-link, .nav-tabs .nav-link.active {
    border-radius: 8px;
    color: #ff0303 !important;
    background-color: #e0936152 !important;
    border-color: #00000000 #00000000 #00000000;
}
.nav-tabs .nav-link:focus, .nav-tabs .nav-link:hover {
    border-color: transparent transparent transparent;
}

a {
    color: #ffb589c2 !important;
    background-color: transparent !important;
}

a:hover {
    color: #ff9389a6 !important;
    text-decoration: unset;
    background-color: transparent !important;
}

.btn-light {
    color: #f7f7f7 !important;
    background-color: #ff59006b !important;
    border-color: transparent !important;
}
.btn-light:hover {
    color: #f7f7f7 !important;
    background-color: #ff006f6b !important;
    border-color: transparent !important;
}

.btn-primary {
    color: #f7f7f7 !important;
    background-color: #54000099 !important;
    border-color: transparent !important;
}

.alert-danger {
    color: #f3a8a0 !important;
    background-color: #5f08006b;
    border-color: transparent;
}

/* Profile Setup */

.progress {
    background-color: #ffc2c24f;
    border-radius: .25rem;
    height: 1rem;
    line-height: 0;
    font-size: .75rem;
}
.badge-info {
    color: #fff;
    background-color: #fd00008c !important;
}

.btn-outline-secondary {
    color: #ff0e00 !important;
    border-color: #ff0e00;
}

.btn-outline-secondary:hover {
    background-color: #f7150059 !important;
    color: #fff !important;
    border-color: #ff0e00;
}

.btn-outline-secondary:not(:disabled):not(.disabled).active, .btn-outline-secondary:not(:disabled):not(.disabled):active, .show>.btn-outline-secondary.dropdown-toggle {
    background-color: #f7150059 !important;
    color: #fff;
    border-color: #ff0e00;
}

.btn-outline-success:not(:disabled):not(.disabled).active, .btn-outline-success:not(:disabled):not(.disabled):active, .show>.btn-outline-success.dropdown-toggle {
    color: #fff;
    background-color: #18bc9c70 !important;
    border-color: #18bc9c;
}

.btn-outline-success:hover {
    color: #fff;
    background-color: #18bc9c70 !important;
    border-color: #18bc9c;
}


.bg-secondary {
    background-color: #e49a54!important;
}

.profile img[data-v-4bd2ff21] {
    height: 3.4em;
    width: 3.4em;
    border: 3px solid #fb770fba;
}

/* Alert box and OTHER */

.alert-success {
    color: #f49bbae0 !important;
    background-color: #aa310370 !important;
    border-color: transparent;
    border-radius: 8px;
}

.left-toggle[data-v-6196efa4] {
    top: -6px;
    right: -2rem;
    width: 2rem;
    height: 0rem;
    line-height: 3.5rem;
}

/* Comments */

.text-muted {
    color: #f1df8a !important;
}

.form-control:disabled, .form-control[readonly] {
    background-color: #5d230a5e;
    opacity: 1;
}

select.form-control[multiple], select.form-control[size], textarea.form-control {
    height: auto;
    background-color: #5d230a5e !important;
    border-color: transparent !important;
    border-radius: 8px;
}

select.form-control[multiple], select.form-control[size], textarea.form-control:hover {
    background-color: #5d230a5e !important;
    border-color: transparent !important;
    border-radius: 8px;
}

a:not([href]) {
    text-decoration: unset !important;
    color: #ffffffbf !important;
    cursor: pointer;
}

a:not([href]):hover {
    text-decoration: unset !important;
    color: #f4a882bf !important;
    cursor: pointer;
}

}
