@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://userstyles.org") , url-prefix("https://userstyles.org") {

  html {
	position: relative !important;
    padding-top: 20px;
    background:
      #fff url("data:image/gif;base64,")
      top center repeat;
  }
  #logo {
    background: transparent url(chrome://stylish/skin/bigicon.png) center left no-repeat;
    padding-left: 32px;
  }
  li {
    margin-bottom: 2px !important;
  }
  #navigation li {
    margin: 0 !important;
  }
  .list-average-rating:before {
    content: " " !important;
  }
  .list-average-rating img {
    width: 12px !important;
    height: 14px !important;
  }
  body {
	min-height: 500px;
	position: relative !important;
	background: white !important;
	font: 75%/170% Verdana, Helvetica, sans-serif !important;
	padding: 12px 0px 50px 0px !important;
	margin: 0px !important;
	color: #333333 !important;
	width: 760px !important;
	position: relative !important;
	margin: 0px auto 35px auto !important;
	-moz-border-radius: 10px;
  }
  
  #logo {
	display: none !important;
  }

  
  #header-bar {
	margin: 0px 0px 0px 0px !important;
	padding: 0px 0px 10px 0px !important;
	position: relative !important;
	background: url("data:image/gif;base64,") top center no-repeat !important;
	-moz-border-radius: 0px !important;
  }
  #body::after {
    content: "Super Style by Drakeshe - drakenscripts.co.cc" !important;
  }
  #account-link {
	color:black !important;
	padding-left:20px !important;
  }
  #trimming {
	background: url("data:image/png;base64,") 
	top center repeat-y !important;
  }
  #left-col {
	width: 695px;
	margin-top: 11px;
  }
	/* main menu */
	
	/* START OLD STUFF */
		#navigation {
			list-style-image: none !important;
			margin: 0 0 0 0 !important;
			 /* float: right !important; */
			clear: none !important;
			padding: 0 0 0 0 !important;
		}
		#navigation li {
			display: inline !important;
			margin: 0 !important;
		}
		#navigation a, #navigation a:visited, #navigation .current-page {
			border: 0px solid transparent !important;
			border-bottom: none !important;
			background: none repeat-x left top !important;
			color: white !important;
			padding: 0px 0px 0px 0px !important;
			text-decoration: none !important;
			font-weight: bold !important;
			-moz-border-radius: 0px 0px 0 0 !important;
		}
		#navigation .current-page {
			border-top: 0px solid black !important;
			border-left: 0px solid black !important;
			border-right: 0px solid black !important;
			background: none repeat-x left top !important;
			color: white !important;
		}
		#navigation a:hover {
			background: none repeat-x left top !important;
		}
		#navigation input:not([type="submit"]):focus {
			background: none repeat-x left top !important;
		}
	/* END OLD STUFF */
	
	
	#navigation {
		margin: 24px 0px 30px -5px !important;
		padding: 0px 20px 0px 20px;
		background: url("data:image/png;base64,") repeat-x left top !important;
		height: 33px !important;
		color: #FFF !important;
		width: 100% !important;
	}
	
	#navigation ul {
		background: url("data:image/png;base64,") repeat-x left top !important;
		float: left !important;
		list-style: none !important;
		display: inline !important;
		margin: 0px 0px 0px 0px !important;
		padding: 0px 0px 0px 0px !important;
		font: bold 12px/100% Verdana, Helvetica, sans-serif !important;
		width: 100% !important;
	}
	#navigation li {
		background: none repeat-x left top !important;
		float: left !important;
		width: auto !important;
		height: 33px !important;
		margin: 0px 1px 0px 0px !important;
		padding: 0px !important;
		display: inline !important;
	}
	.current-page {
		color: white !important;
		background: url("data:image/png;base64,") repeat-x left -33px !important;
	}
	#navigation li a, #navigation li span {
		width: auto !important;
		height: 33px !important;
		background: none repeat-x left -33px !important;
		margin: 6px 7px 13px 7px !important;
		text-decoration: none !important;
		color: #FFF !important;
		display: block !important;
	}
	#navigation li a:visited {
		text-decoration: none !important;
		color: white !important;
	}
	#navigation li:hover {
		text-decoration: none !important;
		background: #a2d0dd !important;
		background: url("data:image/png;base64,") repeat-x left -33px !important;
		color: black !important;
	}
	
	#search{
		margin: 15px 0px 0px 0px !important;
	}
	
	.post-your-style, #sidebar {
		padding: 0px 15px 0px 0px !important;
	}
	p, h1, h2, h3, #screenshots, #style-meta, form {
		padding-left: 15px !important;
	}

	#style_long_description, #css, .linking-note{
		width:600px !important;
	}
