css:
@-moz-document domain("minecraft.net") {
main.site-body,
.bg-white,
.game-carousel .wbg,
.bg-wool-light,
.article-social.ms-social-share.text-center,
.secondary-navigation .sec-nav-bg,
[style="background-color: #F8F5F4;"],
[style="background-color: #FFF;"],
[style="background-color: #FFFFFF;"] {
    background: #222 !important;
    color: #ccc;
}

[style="background-color: #D8D8D8;"] {
    background: #333 !important;
}

[style="background-image: url(/content/dam/minecraft/home/bg-lightwool.png);"],
[style="background-image: url(/content/dam/archive/backgrounds/bg-wool-white.png);"] {
    background-image: url(https://www.minecraft.net/content/dam/minecraft/home/bg-darkwool.png) !important;
}

.gamefeatures .bg-wool-dark {
    filter: invert(85%);
    background-image: url(https://www.minecraft.net/etc.clientlibs/minecraft/clientlibs/main/resources/img/feature-content/bg-wool-light.png) !important;
}

.gamefeatures .bg-wool-dark * {
    color: #444 !important;
}

.gamefeatures .trans-top-dark {
    filter: invert(85%);
    background-image: url(https://www.minecraft.net/etc.clientlibs/minecraft/clientlibs/main/resources/img/feature-content/trans-top-lightwool.png) !important;
}

.gamefeatures .trans-bottom-dark {
    filter: invert(85%);
    background-image: url(https://www.minecraft.net/etc.clientlibs/minecraft/clientlibs/main/resources/img/feature-content/trans-bottom-lightwool.png) !important;
}

.gamefeatures .trans-top-light {
    background-image: url(https://www.minecraft.net/etc.clientlibs/minecraft/clientlibs/main/resources/img/feature-content/trans-top-blackwool.png);
}

.gamefeatures .trans-bottom-light {
    background-image: url(https://www.minecraft.net/etc.clientlibs/minecraft/clientlibs/main/resources/img/feature-content/trans-bottom-blackwool.png);
}

.promo-area .slick-slide .slide,
.gfeatures div {
    background-color: #272727 !important;
}

.game-carousel .gamebox > a:hover {
    color: #eee
}

.text-black, .txt-black {
    color: #ccc !important;
}

.game-carousel .wbg p {
    color: #aaa;
}

.bg-wool-light {
    color: #ccc;
    background: #222 !important;
}

.txt-dark-grass {
    color: #2dca2b !important;
}

.realms-container .txt-dark-grass {
    color: #c95cf5 !important;
}

.blank-row .blank-row {
    background-color: transparent !important;
}
}
