css:
@-moz-document domain("modthesims.info") {
body {
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Helvetica, Arial, sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol";
    font-size: 14px;
    line-height: 22px;
    color: #ffffff;
    background: #080808;
}

div.maincontentspan {
    background-color: #151515;
    padding-left: 1em;
    padding-right: 1em;
}

#menubar {
    background-color: #2b2b2b;
    border: transparent;
    color: #ffffff;
}

.well {
    background-color: #2b2b2b;
    border: 1px solid transparent;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
}

a {
    color: #d8d8d8;
}

.cbp-hrsub.well {
    background-color: #252525;
    border-color: transparent;
}

.nav-list {
    color: #ffffff;
}

.pagination ul>li>a:hover, .pagination ul>li>a:focus, .pagination ul>.active>a, .pagination ul>.active>span, .nav-pills>.active>a, .nav-pills>.active>a:hover, .nav-pills>.active>a:focus, .nav-pills>li>a:hover, .nav-pills>li>a:focus {
    color: #ffffff;
    background-color: #2b2b2b;
}

.nav-pills>li>a {
    border: 1px solid #2b2b2b;
}

.breadcrumb {
    background-color: #2b2b2b;
}

.carousel-inner .alert {
    background-color: #2b2b2b;
}

.muted {
    color: #ffffff;
}

.alert-success {
    background-color: #c3e0b8;
    border-color: #2b2b2b;
    color: #468847;
}

.pagination ul>li>a, .pagination ul>li>span a {
    color: #2b2b2b;
}

.pagination ul>li>a, .pagination ul>li>span [class^="icon-"], [class*=" icon-"] {
    color: #2b2b2b;
}

.alert-success {
    background-color: #252525;
    border-color: #2b2b2b;
    color: #468847;
}

.btn-primary {
    background-color: #2b2b2b;
}

.btn-primary:focus, .btn-primary:hover {
    color: #d4d4d4;
    background-color: #252525;
}

input[type="submit"], input[type="button"] {
    background-color: #2b2b2b;
    color: #ccc;
    border: 2px solid#2b2b2b;
}

.btn:hover, .btn:focus {
    color: #ccc;
    background-color: #252525;
}

.fake-pills>a.active {
    color: #d1d1d1;
    background-color: inherit;
}

.cbp-hrmenu .cbp-hrsub-inner > div a {
    color: #d8d8d8;
}

.downloadinfoblock {
    background-color: #2b2b2b;
}

.downloadblock .well {
    background-color: #353535;
    border-radius: 0px;
    border: none;
}

.alert {
    margin-bottom: 22px;
    background-color: #2b2b2b;
    border: 1px solid #2b2b2b;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    margin-top: 10px;
    margin-right: 15px;
}

.alert, .alert h4 {
    color: #d8d8d8;
}

.btn-info {
    background-color: #2b2b2b;
}

.btn-info:hover {
    background: #2b2b2b;
}

.hero-unit {
    line-height: 33px;
    color: inherit;
    background-color: #636363;
    margin-top: 10px;
}

.breadcrumb>li {
    padding-left: 10px;
}

.navbar-inner {
    background-color: #2b2b2b;
    border: 1px solid #2b2b2b;
}

.navbar .brand {
    padding: 9px 20px 9px;
    color: #ffffff;
    text-shadow: none;
}

.navbar .nav>li>a {
    padding: 9px 15px 9px;
    color: #ffffff;
    text-shadow: none;
}

legend {
    color: #ffffff;
}

.form-search input, .form-inline input, .form-horizontal input, .form-search textarea, .form-inline textarea, .form-horizontal textarea, .form-search select, .form-inline select, .form-horizontal select, .form-search .help-inline, .form-inline .help-inline, .form-horizontal .help-inline, .form-search .uneditable-input, .form-inline .uneditable-input, .form-horizontal .uneditable-input, .form-search .input-prepend, .form-inline .input-prepend, .form-horizontal .input-prepend, .form-search .input-append, .form-inline .input-append, .form-horizontal .input-append {
    margin: 3px 3px 5px 3px;
}
textarea, input[type="text"], input[type="password"], input[type="datetime"], input[type="datetime-local"], input[type="date"], input[type="month"], input[type="time"], input[type="week"], input[type="number"], input[type="email"], input[type="url"], input[type="search"], input[type="tel"], input[type="color"], .uneditable-input {
    background-color: #424242;
    border: 1px solid #bbbbbb;
    color: #fff;
}

.form-search input, .form-inline input, .form-horizontal input, .form-search textarea, .form-inline textarea, .form-horizontal textarea, .form-search select, .form-inline select, .form-horizontal select, .form-search .help-inline, .form-inline .help-inline, .form-horizontal .help-inline, .form-search .uneditable-input, .form-inline .uneditable-input, .form-horizontal .uneditable-input, .form-search .input-prepend, .form-inline .input-prepend, .form-horizontal .input-prepend, .form-search .input-append, .form-inline .input-append, .form-horizontal .input-append {
    margin: 3px 3px 5px 3px;
    background: #424242;
    color: #fff;
}

.visible-phone.cf, .tcat {
    background: #2b2b2b;
    color: #fff;
    padding-left: 10px;
}

.table-striped tbody>tr:nth-child(odd)>td, .table-striped tbody>tr:nth-child(odd)>th {
    background-color: #313131;
}

.pull-right img {
    border-radius: 1000px;
}

#screenshots {
    overflow-y: scroll;
    background-color: #353535;
    min-height: 300px;
}

div.postbit {
    color: #ffffff;
    background-color: #2b2b2b;
    border-bottom: #757575;
    border-right: 0px solid;
    border-left: 1px solid transparent;
}

div.postbittop, div.postbitside {
    color: #ffffff;
    background-color: #2b2b2b;
}

div.postbitmessage {
    color: #ffffff;
    background-color: #7d7d7d;
    border-top: 1px solid transparent;
    border-bottom: 0px solid;
    border-right: 0px solid;
    border-left: 1px solid transparent;
}

.alt1, .alt2 {
    background: #a0a0a0;
}

div.postbitusername {
    background-color: #1d1d1d;
}

.table-striped tbody>tr:nth-child(even)>td, .table-striped tbody>tr:nth-child(even)>th {
    background-color: #444444;
}

.table tbody tr.info>td {
    background-color: #131313!important;
}

div.postbitcontent > div.clearfix {
    background-color: #7d7d7d;
}

#globalWrapper {
    background-color: #151515;
    padding: 1em;
    color: black;
}

#globalWrapper {
    background-color: #151515;
    padding: 1em;
    color: #ffffff;
}
}
