@-moz-document url("https://alumnos.utcj.edu.mx/"), url-prefix("https://alumnos.utcj.edu.mx/Default.aspx"), url-prefix("https://alumnos.utcj.edu.mx/default.aspx") {
/* CODIGO 1 */
/* Login & Default - Aplicables a Login y Default. Ni yo sé cómo funciona.*/
/* Login Form & Panel Avisos */
div#main, .post, table, #divPlataformas, #PanelAvisos, hr{
	background-color: #202124;
    border-color: transparent;
}
/* Login Form & Panel Avisos */
table tr td{
	border-color: transparent;
}
/* Textos */
span, #CphCuerpo_lblRecuperacionCuenta, #CphCuerpo_lblDominio{
	color: #e9e9e9;
}
p{
	color: #bfbfbf;
}
/* Area de Texto */
textarea, input{
	background-color: #D8D8D8;
}
/* Table Head */
th{
	background-color: #5d9927;
    border-color:transparent;
}
/* Buttons */
.BotonVerde{
	background-color: #5d9927;
}

.BotonInicioVerde{
	background-color: #00896a;
}

.BotonInicioGris{
	background-color: #555555;
}
/* Notificaciones */
.CeldaNotificaciones, .BotonBlanco, .BotonBlancoSeleccionado{
	background-color: #202124;
    
}
.BotonBlancoSeleccionado{
	color:#FFFFFF;
}
span#CphCuerpo_lblDatosAlumno b font {
    color: rgb(255, 0, 0);
}
}

@-moz-document url-prefix("https://alumnos.utcj.edu.mx/") {
/* CODIGO 2 */
/* Uso general - CSS para fondo de la página, color del menú y pie estático */

/* Cuerpo general */
body{
    background-color: #353535;
}

/* Menu */
#divMenu{
	background-color: #aeaeae;
}
div.CuerpoMensajeObligatorio, div.TituloMensajeObligatorio, div#cboxLoadedContent, div#cboxContent{
    background-color: #353535;
}
div.CuerpoMensajeObligatorio, div.TituloMensajeObligatorio, h2, p.AvisoPrivacidad{
    color: #fff;
}

/* Footer */
#footer_static, hr{
    border: 1px solid transparent;
    background-color: #353535;
    border-color: transparent;
    color:#353535;
}
}

