.CeldaBajas{
    border-color: #202124;
    border-right: 1px solid #202124;
    border-bottom: 1px solid #202124;
    color: #FFF;
	background-color: #5C5C5C;
}
/* Celdas Notificaciones TItulos*/
.CeldaNotificaciones{
    border-color: #555555;
    border-right: 1px solid #555555;
    border-bottom: 1px solid #555555;
    color: #FFF;
	background-color: #202124;
}
}

