css:
/* i really want this to be global */
/* Vivaldi 3.6 - Candy stripes border for selected tabs */
.svg-tab-selection {
  padding-bottom: 0px !important;
}
.svg-tab-selection line,
.svg-tab-selection rect
{
  stroke-width: 6 !important;
}
.svg-tab-selection line.inner,
.svg-tab-selection rect.inner
{
  stroke: GhostWhite !important;
}
.svg-tab-selection line.outer,
.svg-tab-selection rect.outer
{
  stroke-dasharray: 12, 10 !important;
  stroke-dashoffset: 0 !important;
  stroke: DeepPink !important;
}
