css:
@-moz-document domain('insel-monarchie.de'), url-prefix('http://insel-monarchie.de') {
  
/*----- BACKGROUND COLORS -----*/
 body {
    background: #161616 !important
}
/*----- TABLES & BORDERS -----*/
 td {
    font-family: Verdana, Helvetica, sans-serif;
    font-size:11px;
    color: #000000;
}
td.menu {
    background : #F1EBDA;
    border : 1px solid #646464;
}
td.header {
    font-size:12px;
    vertical-align:center;
    background : #F1EBDA;
    height:30px;
    border : 1px solid #322B23;
    background:#cbba99 url(http://insel-monarchie.de/images/header.gif) repeat-x;
}
td.subheader {
    font-size:12px;
    background : #F1EBDA;
    height:25px;
    border : 1px solid #322B23;
    background:#cbba99 url(http://insel-monarchie.de/images/header.gif) repeat-x;
}
td.karte {
    font-family : Arial, Helvetica, sans-serif;
    font-size : 9px;
    color : White;
}
.wline {
    background-color:#f1ebda;
    padding:3px;
}
table.main {
    padding:0px;
}
table.omain {
    font-family : Verdana, Helvetica, sans-serif;
    font-size : 10px;
    color : Black;
}
A.main, A.main:LINK, A.main:VISITED, A.main:ACTIVE {
    font-family: Verdana, Helvetica, sans-serif;
    font-size:11px;
    color : Black;
}
A.archiv, A.archiv:LINK, A.archiv:VISITED, A.archiv:ACTIVE {
    font-family: Verdana, Helvetica, sans-serif;
    font-size:11px;
    color : #666666;
}
A:HOVER {
    text-decoration : underline;
}
a.bl {
    font-size:12px;
    color: #000000;
}
.main {
    font-family: Verdana, Helvetica, sans-serif;
    font-size:11px;
    color : black;
}
.stats {
    font-size : 10px;
    color : #6F7893;
    font-family : Verdana, Helvetica, sans-serif;
}
.flags {
    border : 1px solid #333333;
}
.karte {
    font-family : Arial, Helvetica, sans-serif;
    font-size : 9px;
    color : White;
}
A.karte, A.karte:LINK, A.karte:karte, A.karte:ACTIVE {
    font-family : Arial, Helvetica, sans-serif;
    font-size : 9px;
    color : White;
    text-decoration : none;
}
.footer {
    font-family : Verdana, Helvetica, sans-serif;
    font-size : 9px;
}
t:HOVER {
    text-decoration : none;
}
select.field {
    border: 1px solid #5d4c2f;
    border-top-color: #5d4c2f;
    border-left-color: #5d4c2f;
    padding: 2px;
    background-color: #ebe9e3;
    font:normal 10px Verdana, Helvetica, sans-serif;
    width: 40px;
    height:18px;
    text-align:left;
}
input.textfield {
    border: 1px solid #c9a584;
    border-top-color: #5d4c2f;
    border-left-color: #5d4c2f;
    padding: 2px;
    background-color: #fff7e1;
    background: #fff7e1 url(http://insel-monarchie.de/images/textfield.gif) repeat-x;
    font:normal 10px Verdana, Helvetica, sans-serif;
    width: 40px;
    height:18px;
    text-align:center;
}
input.textfield_bl {
    border: none;
    background: none;
    font:normal 12px Verdana, Helvetica, sans-serif;
    font-weight:bold;
    color:#000000;
    width: 40px;
    height:18px;
    text-align:center;
}
input.textfield_bl:disabled {
    border: none;
    background: none;
    font:normal 12px Verdana, Helvetica, sans-serif;
    font-weight:bold;
    color:#000000;
    width: 40px;
    height:18px;
    text-align:center;
}
input.textfield:focus {
    background-image:none;
    background-color: #ffffff;
}
.field {
    background-color: #F1EBDA;
    width: 30px;
    border: 1px solid #333333;
    font-family:Verdana, Helvetica, sans-serif;
    font-size : 10px;
    text-align:center;
}
.field_new {
    width: 40px;
    height:18px;
    border: 1px solid #5d4c2f;
    border-top-color: #c9a584;
    border-left-color: #c9a584;
    padding: 2px 0px;
    font-family:Verdana, Helvetica, sans-serif;
    font-size : 10px;
    text-align:center;
    background:#F1EBDA url(http://insel-monarchie.de/images/button.gif) repeat-x;
}
.field_text {
    border: 1px solid #5d4c2f;
    border-top-color: #c9a584;
    border-left-color: #c9a584;
    padding: 2px 0px;
    font-family:Verdana, Helvetica, sans-serif;
    font-size : 10px;
    background:#ffffbc url(http://insel-monarchie.de/images/button.gif) repeat-x;
}
.field2 {
    background-color: #F1EBDA;
    width: 50px;
    border: 1px solid #333333;
    font-family:Verdana, Helvetica, sans-serif;
    font-size : 10px;
}
.field3 {
    background-color: #F1EBDA;
    width: 320px;
    border: 1px solid #333333;
    font-family : Verdana, Helvetica, sans-serif;
    font-size : 10px;
}
.field4 {
    background-color: #F1EBDA;
    width: 40px;
    border: 1px solid #333333;
    font-family : Verdana, Helvetica, sans-serif;
    font-size : 10px;
}
.fieldlogin {
    background-color: #F1EBDA;
    width: 150px;
    border: 1px solid #333333;
    font-family:Verdana, Helvetica, sans-serif;
    font-size : 10px;
}
.select {
    background-color: #F1EBDA;
    width: 100px;
    border: 1px solid #333333;
    font-family : Verdana, Helvetica, sans-serif;
    font-size : 10px;
}
.button {
    background-color: #F1EBDA;
    width: 70px;
    border: 1px solid #333333;
    font-family:Verdana, Helvetica, sans-serif;
    font-size : 10px;
}
.button_post {
    background-color: #F1EBDA;
    width: 120px;
    border: 1px solid #333333;
    font-family : Verdana, Helvetica, sans-serif;
    font-size : 10px;
}
.buttonsmall {
    background-color: #F1EBDA;
    width: 30px;
    border: 1px solid #333333;
    font-family : Verdana, Helvetica, sans-serif;
    font-size : 10px;
}
.button_new {
    border: 3px double #5d4c2f;
    border-top-color: #c9a584;
    border-left-color: #c9a584;
    padding: 2px 0px;
    background:#F1EBDA url(http://insel-monarchie.de/images/button.gif) repeat-x;
    font-size:12px;
    text-align:center;
    font-weight:bold;
    color:#000000;
}
.button_disabled {
    border: 3px double #5d4c2f;
    border-top-color: #c9a584;
    border-left-color: #c9a584;
    padding: 2px 0px;
    background:#F1EBDA url(http://insel-monarchie.de/images/button_disabled.gif) repeat-x;
    font-size:12px;
    text-align:center;
    font-weight:bold;
    color:#808080;
}
.archiv {
    font-family : Verdana, Helvetica, sans-serif;
    font-size : 10px;
    color : #666666;
}
.res_text {
    FONT-SIZE: 12px;
    FONT-FAMILY: Verdana, Helvetica, sans-serif;
    color : #DFC9AA;
}
td.res_text {
    color : #DFC9AA;
}
.copyright {
    FONT-SIZE: 10px;
    FONT-FAMILY: Verdana, Helvetica, sans-serif;
}
.footer {
    FONT-SIZE: 7px;
    FONT-FAMILY: Verdana, Helvetica, sans-serif;
    color : #DFC9AA;
}
A.footer:LINK, A.footer:VISITED, A.footer:ACTIVE {
    color : #DFC9AA;
}
td.rs {
    text-align:right;
    width:5%;
}
td.hilfe_header {
    font-family:Verdana, Helvetica, sans-serif;
    font-size:16px;
    font-weight:bold;
    text-align:center;
    background : #b8aa91;
    border : 1px solid #322B23;
}
td.hilfe {
    font-family:Verdana, Helvetica, sans-serif;
    font-size:12px;
}
td.hilfetext {
    font-family:Verdana, Helvetica, sans-serif;
    font-size:12px;
}
td.hilfetext:first-letter {
    float: left;
    font-size:36px;
    letter-spacing:102px;
}
a.hilfetext {
    font-family:Verdana, Helvetica, sans-serif;
    font-size:14px;
}
span.hilfetext {
    font-family:Verdana, Helvetica, sans-serif;
    font-size:12px;
}
span.hilfetext:first-letter {
    float: left;
    font-size:36px;
    letter-spacing:102px;
}
}
