css:
@-moz-document url-prefix("https://www.bitchute.com/"), url-prefix("http://www.bitchute.com/") {
#nav-menu { margin-top: -10 !important; transform: translateY(-45px); position: absolute; z-index: 9999;}
#nav-menu:hover { margin-top: -10 !important; transform: translateY(-10px); position: absolute;z-index: 99999}
div.container:nth-child(10) { transform: translateY(-50px); }

 #nav-top-menu .search-box {
    border: thin solid #373538;
    background: #121212;
}
#nav-top-menu .search-box {
    position: absolute;
    top: 0;
    left: 200px;
    margin: 15px 0 27px 20px;
    width: 300px;
}

#nav-top-menu .search-box .form-control {
    position: relative;
    padding: 6px 22px;
    height: 30px;
    background: 0 0;
    border: none;
    box-shadow: none;
    transition: all .3s ease-in-out .05s;
}

#page-detail{z-index: 10;}

#nav-top-menu { background-image:none !important; }

.faa-vertical.animated, .faa-vertical.animated-hover:hover, .faa-parent.animated-hover:hover>.faa-vertical {
    -webkit-animation: none;
    animation: none;
}

img.logo-icon.hidden-sm.hidden-md.hidden-lg.hidden-xl {  width: 130px;}

#nav-top-menu .logo img {
    margin: 17px 0;
    height: 28px;
}

#nav-top-menu .user-menu .user-menu-icon {
    position: absolute;
    top: 2px;
    left: -1px;    
}

div.user-link:nth-child(1) {  display: none !important; }

.svg-inline--fa.fa-w-16, #userdropdown > img:nth-child(1), .svg-inline--fa.fa-w-14 {width: 15px;}

#nav-top-menu {height: 50px !important}

body {	background: #211F22;}
#page-bar, #carousel, .section { border-bottom: 3px solid #211F22;}

#nav-menu-buffer {padding-top: 0px;}
}

@-moz-document url-prefix("https://www.bitchute.com/video"), url-prefix("http://www.bitchute.com/video") {
#nav-menu { margin-top: -10 !important; transform: translateY(-45px); position: absolute; z-index: 9999;}
#nav-menu:hover { margin-top: -10 !important; transform: translateY(-10px); position: absolute;z-index: 99999}
