#nav-menu-buffer {padding-top: 61px;}

#page-bar .page-title {	text-align: center;font-size: 16px;}

#main-content > .container {width: 100vw;
	margin: 0;}

html.fontawesome-i2svg-active.fontawesome-i2svg-complete body div#wrapper div#main-content div.container div#page-detail.row div.col-xs-12 div.tab-scroll-outer { display: none !important;   }

.video-container .plyr {
	margin-left: auto;
	margin-right: auto;
    max-height: calc(100vh - 50px);
    width: auto;
    max-width: calc(90vw);}

#player {    max-height: calc(100vh - 50px) !important; height: 100% !important}

#page-detail > div > .tab-content > .tab-pane > .row > div {
    width: 100%;
    float: none;}

.video-container .video-publish-date {
    font-size: 1em;
    font-weight: bold;
    color: hsl(0, 0%, 74%);}

div.row:nth-child(3) {
	width: 75%;
	margin-left: auto;
	margin-right: auto;
}

div.row:nth-child(4){
	width: 75%;
	margin-left: auto;
	margin-right: auto;
}

html.fontawesome-i2svg-active.fontawesome-i2svg-complete body div#wrapper div#main-content div.container div#page-detail.row div.col-xs-12 div.tab-content div#video-watch.tab-pane.active div.row div.col-xs-12.col-sm-4.col-md-3.sidebar {	width: 75%;
	margin-left: auto;
	margin-right: auto;
    
}

}
