css:
@-moz-document domain("qanon.pub") {
body {
	background: #1d1f21;
	color: whitesmoke;
}

header.row,
button {
	background: #282a2e;
	color: #eeeeee;
}

a {
	color: #7091de;
}

time {
	background: inherit !important;
	color: #b98eff !important;
}

.sticky time {
	color: whitesmoke !important;
}

article {
	background: #282a2e !important;
}

article blockquote {
	background: #1d1f21 !important;
}

article .userid {
	border-radius: 2px;
}

body > header input[type="search"] {
	border-radius: 5px;
}

input[type="search"] {
	background: transparent !important;
	color: whitesmoke !important;
}

::placeholder {
	color: whitesmoke !important;
}

#count {
	background: #121212;
	padding: 7px 5px 5px 5px;
	margin: 0px 5px 0px 7px;
	border-radius: 5px;
	border: solid whitesmoke 1px;
}

.tgl-btn {
	background-color: transparent !important;
	border: whitesmoke solid 1px !important;
	margin-left: 10px;
}

.dialog.open {
	background: #121212;
}

.dialog.open > h1,
h3,
h5 {
	color: #00FFA5;
}

.dialog.open > h2,
h4,
h6 {
	color: #FF9700;
}

.tgl-l:checked + .tgl-btn {
	background: #29db3b !important;
}
}
