css:
@-moz-document domain("convertingcolors.com") {
body {
	color: #ccc;
	background: #000;
}
details > summary, .nav-dropdown-content a {
	color: #ccc;
}
th {
	border-color: #222;
}
code, .o {
    background-color: hsl(0deg 0% 0%);
}
.abc {
    background-color: hsl(0, 0%, 0%);
    box-shadow: 0 1px 4px 1px hsla(0, 0%, 100%, .1);
}
.abc:hover, .footerAbc:hover {
    box-shadow: 5px -5px 0 -3px hsl(0, 0%, 0%), 5px -5px var(--current-color-rectange-one), 10px -10px 0 -3px hsl(0, 0%, 0%), 10px -10px var(--current-color-rectange-two), 15px -15px 0 -3px hsl(0, 0%, 0%), 15px -15px var(--current-color-rectange-three);
}
#mc_embed_signup .button:hover, .contentForm input[type="button"]:hover, .contentForm input[type="submit"]:hover, input[type=button].colorButton:hover, .nav-dropdown:hover .nav-dropdown-button {
    background-color: hsla(0, 0%, 100%, .1);
    color: #ccc;
}
.nav-dropdown-content a:hover, .nav-dropdown-content a:active {
    background-color: hsl(0, 0%, 6%);
}
.nav-dropdown-content {
    background-color: #0a0a0a;
    box-shadow: 0 1px 4px 1px hsla(0, 0%, 100%, .1);
}
div.r:nth-of-type(1) > div.c50:nth-of-type(1) > .p {
	background: #fff;
}
.button-code-copy {
    background: #000;
    color: #ccc;
    box-shadow: 0 1px 4px 1px hsla(0, 0%, 100%, .1);
}
.twitter-follow a, .twitter-follow a:visited, .twitter-follow a:active, .twitter-follow a:hover, .twitter-follow span, .twitter-follow p {
	color: #ccc;
}
#onetrust-consent-sdk #onetrust-pc-sdk, #onetrust-consent-sdk #search-container, #onetrust-consent-sdk #onetrust-pc-sdk .ot-switch.ot-toggle, #onetrust-consent-sdk #onetrust-pc-sdk group-toggle .checkbox, #onetrust-consent-sdk #onetrust-pc-sdk #pc-title:after {
	background: #000;
}
#onetrust-consent-sdk #onetrust-pc-sdk h3, #onetrust-consent-sdk #onetrust-pc-sdk h4, #onetrust-consent-sdk #onetrust-pc-sdk h5, #onetrust-consent-sdk #onetrust-pc-sdk h6, #onetrust-consent-sdk #onetrust-pc-sdk p, #onetrust-consent-sdk #onetrust-pc-sdk #vendors-list-container .vendor-options p, #onetrust-consent-sdk #onetrust-pc-sdk #pc-policy-text, #onetrust-consent-sdk #onetrust-pc-sdk #pc-title, #onetrust-consent-sdk #onetrust-pc-sdk .leg-int-title, #onetrust-consent-sdk #onetrust-pc-sdk .leg-int-sel-all-hdr span, #onetrust-consent-sdk #onetrust-pc-sdk #hosts-list-container .vendor-host, #onetrust-consent-sdk #onetrust-pc-sdk #ot-filter-modal #modal-header, #onetrust-consent-sdk #onetrust-pc-sdk .ot-checkbox label span, #onetrust-consent-sdk #onetrust-pc-sdk #vendors-list #select-all-container p, #onetrust-consent-sdk #onetrust-pc-sdk #vendors-list #vendors-list-title, #onetrust-consent-sdk #onetrust-pc-sdk #vendors-list .back-btn-handler p, #onetrust-consent-sdk #onetrust-pc-sdk #vendors-list .vendor-title, #onetrust-consent-sdk #onetrust-pc-sdk #vendors-list #vendors-list-container .consent-category, #onetrust-consent-sdk #onetrust-pc-sdk .ot-leg-btn-container .ot-inactive-leg-btn, #onetrust-consent-sdk #onetrust-pc-sdk .ot-label-status, #onetrust-consent-sdk #onetrust-pc-sdk .ot-chkbox label span, #onetrust-consent-sdk #onetrust-pc-sdk #clear-filters-handler {
	color: #969696;
}
#onetrust-pc-sdk .ot-accordion-layout.category-item:first-of-type, #onetrust-pc-sdk .ot-accordion-layout.category-item {
	border-color: #191919;
}
#onetrust-pc-sdk .accordion-text .ot-switch-label, #onetrust-pc-sdk .ot-accordion-layout.category-item .ot-switch-label {
    background-color: #020101;
	border-color: #222;
}
#onetrust-pc-sdk .ot-switch-inner:before {
	background: #25292d;
}
a.powered-by-logo {
	filter: invert(100);
}
#onetrust-consent-sdk #onetrust-pc-sdk .ot-accordion-pc-container.accordion-text, #onetrust-consent-sdk #onetrust-pc-sdk .accordion-text .cookie-subgroup-toggle .ot-switch.ot-toggle {
	background: #191919;
}
#onetrust-pc-sdk .ot-switch-nob {
	background: #8d8d8d;
	border-color: #8d8d8d;
}
#onetrust-pc-sdk #ot-content::-webkit-scrollbar-thumb, #onetrust-pc-sdk .ot-group-options::-webkit-scrollbar-thumb, #onetrust-pc-sdk #vendor-list-content::-webkit-scrollbar-thumb {
    background: #282828;
}
details[open] > summary::before {
    background: repeating-linear-gradient(-45deg,var(--current-color),var(--current-color) 5px,transparent 5px,transparent 10px) #000;
}
select {
    filter: invert(100);
}
option {
	background: #000;
	color: #fff;
}
}
