.fusion-text a {
	color: #732828 !important;
	font-weight:600;
}
.dataTables_wrapper .tablepress {
	min-width:100%;
}
.fusion-copyright-notice {
	display:none;
}
.dataTables_scrollHead {
	position: sticky !important;
	top: 0;
	display: block;
}
.dataTables_filter {
	float: none;
	white-space: nowrap;
	padding: 5px 0;
	text-align:center;
}
.dataTables_filter label {
	display:inline-block;
}
.dataTables_wrapper .dataTables_filter input {
	margin-left: .5em;
	border: 0 none;
	box-shadow: 0px 1px 2px #0005 inset;
	padding:4px;
}
table.tablepress th {
	border-radius: 0 0 0;
	border: 1px solid #0005;
	background-color: #64423A;
	/* border-right: 1px solid; */
	transition: all 0.3s;
	vertical-align: top;
	font-family: Raleway, Arial, Helvetica, sans-serif !important;
}
table.tablepress {
	border: 0;
}
table.tablepress td {
	border-right: 1px solid #0003;
	border-left: 1px solid #0003;
	border-radius: 0 0 0;
	transition:all 0.3s;
}
table.tablepress .even:hover td {
	background-color: #f2be8a;
}
table.tablepress .odd:hover td {
	background-color: #f2be8a;
}
table.tablepress td a {
	color: #000;
	transition: 0s all;
	display: block;
}
table.tablepress .even td {
	background-color: #dda872;
}
table.tablepress .odd td {
	background-color: #ca9662;
}
table.tablepress td a::before {
	content:"";
	display: inline-block;
	border-top: 2px solid #fff;
	border-right: 2px solid #fff;
	border-left: 2px solid transparent;
	border-bottom: 2px solid transparent;
	transform: rotate(45deg);
	margin-right: 1em;
}
.fusion-modal h5, .fusion-tb-footer h5, .fusion-tb-page-title-bar h5, .fusion-title h5, .fusion-widget-area h5, .post-content h5, .tablepress-table-description, .fusion-modal h1, .fusion-page-title-bar h1, .fusion-tb-footer h1, .fusion-tb-page-title-bar h1, .fusion-title h1, .fusion-widget-area h1, .post-content h1, .search-page-search-form h1 {
	font-family: Raleway, Arial, Helvetica, sans-serif;
}
hr {
	border-bottom: 3px solid #6f2a2a;
	margin: 0;
	height: 1px;
	border-top: 0 none;
	border-left: 1em solid #f1d9c0;
	border-right: 1em solid #f1d9c0;
}
.trattoall {
	background:#333;
}
