.main {
    background: #FFF url("https://raw.githubusercontent.com/Lachney/TypeRacer-Halloween-Theme/main/img/bgmain.cache.png") no-repeat;
}

.footer {
    background: #BD6700 url("https://raw.githubusercontent.com/Lachney/TypeRacer-Halloween-Theme/main/img/bg.cache.png") repeat-x 0 -92px;
}

body {
    background: #BD6700 url("https://raw.githubusercontent.com/Lachney/TypeRacer-Halloween-Theme/main/img/bg.cache.png") repeat-x scroll 0 0;
}

.container {
    background: url("https://raw.githubusercontent.com/Lachney/TypeRacer-Halloween-Theme/main/img/topleft.cache.png") no-repeat;
}

.countodwnPopup {
    background: #C56600
}

a {
    color: black;
}

a:hover {
    color: #ff7232;
}

.navigation a:hover {
    background-color: #ff7232;
}

.trPopupDialog {
    background-color: #ffa783;
}

.trPopupDialog {
    color: black
}

.countdownPopup {
    background-color: #ffa37d;
}
}
