css:
@-moz-document domain(news.ycombinator.com) {
    .commtext, .commtext a { color: #000 !important; }

    .commtext:not(.c00)::before {
        display: block;
        font-weight: bold;
        font-size: 1.5em;
        margin-bottom: 0.5rem;
    }

    .commtext.c5a::before { content: '+1 comment'; }
    .commtext.c73::before { content: '+2 comment'; }
    .commtext.c82::before { content: '+3 comment'; }
    .commtext.c88::before { content: '+4 comment'; }
    .commtext.c9c::before { content: '+5 comment'; }
    .commtext.cae::before { content: '+6 comment'; }
    .commtext.cbe::before { content: '+7 comment'; }
    .commtext.cce::before { content: '+8 comment'; }
    .commtext.cdd::before { content: '+9 top comment ‚ù§'; }
}
