css:
@-moz-document domain("forum.donanimhaber.com")
{
  /* Simple dark mode css*/
  html {
      filter: invert(1) hue-rotate(180deg);
  }
  html img,
  html iframe,
  html object,
  html video {
      filter: invert(1) hue-rotate(180deg);
  }
  
  /* hidden advert */
  [id*=ads],
  [class*=ads],
  [id*=reklam],
  [class*=reklam] {
      display: none !important;
      height: 0 !important;
      overflow: hidden !important;
      visibility: hidden !important;
  }

  /* edits */
  body {
      background: /*[[arkaplan-rengi]]*/;
  }
}
