css:
@-moz-document domain("anime-kage.eu") {
.btn-outline-main {
    color: #04fd68
}

a.btn-outline-main:hover {
    color: #00e7ff
}

.btn-outline-main:not(:disabled):not(.disabled).active,
.btn-outline-dark:not(:disabled):not(.disabled):active,
.show > .btn-outline-main.dropdown-toggle {
    background-color: #00ff08;
    border-color: #278a0f;
}

.btn-outline-main:not(:disabled):not(.disabled).active,
.btn-outline-dark:not(:disabled):not(.disabled):active,
.show > .btn-outline-main.dropdown-toggle {
    color: #0547ff;
}

a,
.btn-link {
    color: #fd5200;
}

/* Notificare Noua! */
a.notification-message:hover {
    color: #24bb00;
}

a:hover {
    color: #700
}

/*Asta e pt pagina de creat banere, de a se intelege scrisul de acolo care nu se vedea */
.margin-bottom-double.gray-container {
    color: #a01800;
}

/* width */
::-webkit-scrollbar {
    width: auto;
}

/* Track */
::-webkit-scrollbar-track {
    box-shadow: inset 0 0 10px #3a010b;
    border-radius: 10px;
}

/* Handle */
::-webkit-scrollbar-thumb {
    background: red;
    border-radius: 10px;
}

/* Handle on hover */
::-webkit-scrollbar-thumb:hover {
    background: #b30000;
}


#thank-episode.btn.btn-primary:hover {
    color: #b3ff00;
}

a.notification-message.seen-message {
    color: #ff1567;
}

/* Notificare Deschisa */
a.notification-message.seen-message:hover {
    color: #0078ff
}


/*Culoare Text Manga*/
.text-frame {

    color: #ffac00!important;
}


