css:
@-moz-document regexp(".+://(?:[^/]+\\.)?(?:(?:stackoverflow|serverfault|superuser|stackexchange|askubuntu|stackapps)\\.com|mathoverflow\\.net)/?.*")
{
  .s-prose
  {
    line-height: 1.3!important;
  }

  .s-prose pre.s-code-block, .s-prose pre:not(.s-code-block)
  {
    line-height: 1.3!important;
    padding: 12px 8px!important;
    border-radius: 3px!important;
  }

  .s-prose pre.s-code-block code, .s-prose pre:not(.s-code-block) code
  {
    background-color: transparent!important;
    padding: 0!important;
  }

  .s-prose *:not(.s-code-block) > code
  {
    padding: 1px 5px!important;
    border-radius: 0px!important;
  }

  .comment-text code
  {
    padding: 1px 5px!important;
  }
  
  /* old paragraph spacing */
  .s-prose p, .s-prose ol, .s-prose ul, .s-prose blockquote, .s-prose hr
  {
    margin-bottom: 15px!important;
  }

  .s-prose pre
  {
    margin-bottom: 13px!important;
  }

  .s-prose ol li, .s-prose ul li
  {
    margin-bottom: 7.5px!important;
  }

  .s-prose ol li:last-child, .s-prose ul li:last-child
  {
    margin-bottom: 0!important;
  }

  .s-prose h1
  {
    margin-bottom: 21px!important;
  }

  .s-prose h2
  {
    margin-bottom: 19px!important;
  }

  .s-prose h3
  {
    margin-bottom: 17px!important;
  }

  .s-prose h4
  {
    margin-bottom: 15px!important;
  }

  .s-prose blockquote *:last-child
  {
    margin-bottom: 0!important;
  }
}
