css:
@-moz-document regexp('https://wx.zsxq.com/dweb2/index/group/.*') {

body {
    background-color: #000 !important;
}

.topic-container .answer-content-container .answer, .topic-container .answer-content-container .content, .topic-container .solution-content-container .content, .topic-container .talk-content-container .content, .topic-container .task-content-container .content {
    font-size: 14px!important;
    line-height: 22px!important;
}


.index-container .group-list-container {
    position: fixed;
    margin: 10px 100px 0 0;
    float: left;
    width: 310px;
    padding-left: 50px;
    color: #8b8e9d;
    box-sizing: border-box;
    height: calc(100% - 80px);
    overflow: hidden;
}

.index-container .topic-flow-container {
    margin: 0 auto;
    padding: 50px 0 0;
    width: 1820px;
}

.index-container .topic-flow-container {
    margin: 0 auto;
    padding: 50px 0 0;
    /* width: 1220px; */
}

/** 内容总宽度 **/
.index-container .main-content-container {
    margin-left: 300px!important;
    float: left;
    width: 85%;
    margin: 10px 10px 0 0;
}

/** 年导航选择 **/
.index-container .month-selector {
    float: left;
    width: 46px;
    margin-top: 10px;
    position: fixed;
    margin-left: 0px; 
    font-family: PingFangSC-Regular;
    font-size: 12px;
    -webkit-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
    transform: translateZ(0);
    transition: all .3s ease-out;
    -webkit-transition: all .3s ease-out;
    -o-transition: all .3s ease-out;
}

/** 评论框 **/
.index-container .group-preview-container .group-preview-wrapper {
    width: 300px;
    height: 100%;
    overflow-y: scroll;
    padding-right: 17px;
    box-sizing: content-box;
    display: none;
}

.index-container .comment-box {
    margin-bottom: 0px;
    padding-top: 8px;
    max-height: 150px;
    overflow-y: scroll;
}

}
